import React, { useEffect, useMemo, useState } from "react";
import { useCart } from "../context/CartContext";
import { useTranslation } from "react-i18next";
import { supabase } from "../lib/supabaseClient";
import { useCurrency } from "../context/CurrencyContext";
import { formatMoneyFromIDR } from "../utils/currency";

export default function Checkout() {
  const { t, i18n } = useTranslation();
  const { items, clear } = useCart();
  const { fx, currency, locale } = useCurrency();

  const [date, setDate] = useState(() => new Date().toISOString().slice(0,10));
  const [audience, setAudience] = useState("domestic"); // or 'foreign'
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState([]); // [{booking_id, public_code, total_idr}]

  const totalIDR = useMemo(() => items.reduce((s,i)=> s + (i.price*i.pax*i.qty), 0), [items]);

  const submit = async () => {
    if (items.length === 0) return;
    setLoading(true);
    try {
      const created = [];
      for (const it of items) {
        const { data, error } = await supabase.rpc('place_order', {
          _package_id: it.id,
          _date: date,
          _pax: it.pax || 1,
          _audience: audience,
          _customer_name: name || 'Guest',
          _email: email || 'guest@example.com',
          _phone: phone || '',
          _notes: notes || '',
          _currency: currency,
          _items: JSON.stringify([]) // add-on contoh kosong
        });
        if (error) throw error;
        created.push(data[0]);
      }
      setResults(created);
      // optional: kosongkan keranjang setelah order (atau setelah bayar)
      // clear();
    } catch (e) {
      console.error(e);
      alert(e.message || 'Checkout failed');
    } finally {
      setLoading(false);
    }
  };

  const createPayment = async (booking_id, provider='xendit') => {
    try {
      const res = await fetch(`/functions/v1/create-payment`, {
        method: 'POST',
        headers: { 'Content-Type':'application/json' },
        body: JSON.stringify({ booking_id, provider })
      });
      const out = await res.json();
      if (!res.ok) throw new Error(out.error || 'Failed');
      if (out.payment_url) window.open(out.payment_url, '_blank');
    } catch (e) {
      console.error(e);
      alert(e.message);
    }
  };

  return (
    <div className="container mt-6 grid md:grid-cols-3 gap-6">
      <div className="md:col-span-2 card p-4">
        <h1 className="text-2xl font-bold mb-4">Checkout</h1>

        <div className="grid md:grid-cols-2 gap-3">
          <div className="space-y-2">
            <label className="text-sm">Tanggal Trip</label>
            <input type="date" className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={date} onChange={e=>setDate(e.target.value)} />
          </div>
          <div className="space-y-2">
            <label className="text-sm">Tipe Tamu</label>
            <select className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={audience} onChange={e=>setAudience(e.target.value)}>
              <option value="domestic">Domestik (ID)</option>
              <option value="foreign">Foreign</option>
            </select>
          </div>

          <div className="space-y-2">
            <label className="text-sm">Nama</label>
            <input className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={name} onChange={e=>setName(e.target.value)} />
          </div>
          <div className="space-y-2">
            <label className="text-sm">Email</label>
            <input type="email" className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={email} onChange={e=>setEmail(e.target.value)} />
          </div>
          <div className="space-y-2 md:col-span-2">
            <label className="text-sm">Telepon/WA</label>
            <input className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={phone} onChange={e=>setPhone(e.target.value)} />
          </div>
          <div className="space-y-2 md:col-span-2">
            <label className="text-sm">Catatan</label>
            <textarea rows="3" className="border rounded-xl px-3 py-2 dark:bg-slate-900 w-full" value={notes} onChange={e=>setNotes(e.target.value)} />
          </div>
        </div>

        <div className="mt-4">
          <button className="btn btn-primary" disabled={loading || items.length===0} onClick={submit}>
            {loading ? "Memproses..." : "Buat Pesanan"}
          </button>
        </div>

        {results.length > 0 && (
          <div className="mt-6">
            <h2 className="font-semibold mb-2">Pesanan Berhasil Dibuat</h2>
            <ul className="space-y-2">
              {results.map(r => (
                <li key={r.booking_id} className="border rounded-xl p-3 flex items-center justify-between">
                  <div>
                    <div className="text-sm">Booking ID:</div>
                    <div className="font-mono text-xs">{r.booking_id}</div>
                    <div className="text-sm mt-1">Kode Publik:</div>
                    <div className="font-mono text-xs">{r.public_code}</div>
                    <div className="mt-1 text-sky-600 font-semibold">
                      Total: {formatMoneyFromIDR(r.total_idr, currency, fx, locale)}
                    </div>
                  </div>
                  <div className="flex flex-col gap-2">
                    <button className="btn btn-outline" onClick={()=>createPayment(r.booking_id,'xendit')}>Bayar (Xendit)</button>
                    <button className="btn btn-outline" onClick={()=>createPayment(r.booking_id,'midtrans')}>Bayar (Midtrans)</button>
                  </div>
                </li>
              ))}
            </ul>
            <div className="mt-3">
              <button className="btn" onClick={clear}>Kosongkan Keranjang</button>
            </div>
          </div>
        )}
      </div>

      <aside className="card p-4 h-max">
        <h2 className="font-semibold mb-3">Ringkasan</h2>
        <div className="space-y-2">
          {items.map(it => (
            <div key={it.id + "-" + it.pax} className="flex items-center justify-between">
              <div className="text-sm">{it.title} — {it.pax} pax</div>
              <div className="text-sm">
                {formatMoneyFromIDR(it.price*it.pax, currency, fx, locale)}
              </div>
            </div>
          ))}
        </div>
        <div className="border-t mt-3 pt-3 flex items-center justify-between">
          <span className="font-medium">Total</span>
          <span className="font-semibold">
            {formatMoneyFromIDR(totalIDR, currency, fx, locale)}
          </span>
        </div>
      </aside>
    </div>
  );
}
