import React, { useMemo, useState } from "react";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import HeroBackground from "../components/HeroBackground";
import ParallaxStrip from "../components/ParallaxStrip";
import { toIDR } from "../utils/format";
import { useCart } from "../context/CartContext";
import data from "../data/packages.json";

const heroImages = ["/hero1.jpg","/hero2.jpg","/hero3.jpg","/hero4.jpg","/hero5.png","/hero6.png"];

// animasi
const listVariants = { hidden: { opacity: 0 }, show: { opacity: 1, transition: { staggerChildren: 0.08, delayChildren: 0.05 } } };
const itemVariants = { hidden: { opacity: 0, y: 10 }, show: { opacity: 1, y: 0, transition: { duration: 0.3, ease: "easeOut" } } };
const heroText = { hidden: { opacity: 0, y: 18 }, show: { opacity: 1, y: 0, transition: { duration: 0.55, ease: "easeOut" } } };

const LABELS = {
  en: { popular: "Popular Packages", calcFor: "Calculate price for", paxSuffix: "/pax", addToCart: "Add to Cart" },
  id: { popular: "Paket Populer", calcFor: "Hitung harga untuk", paxSuffix: "/pax", addToCart: "Tambah ke Keranjang" },
  ja: { popular: "人気パッケージ", calcFor: "料金を計算", paxSuffix: "/人", addToCart: "カートに追加" }
};

export default function Home() {
  const { t, i18n } = useTranslation();
  const lang = (i18n.language || "en").split("-")[0];
  const L = (obj) => (obj && (obj[lang] ?? obj.en)) || obj;
  const { addItem } = useCart();
  const [pax, setPax] = useState(2);

  const gallery = useMemo(() => {
    // pakai subset biar efisien (12 gambar)
    const n = 12;
    return Array.from({ length: n }, (_, i) => `/${i + 1}.png`);
  }, []);

  return (
    <>
      {/* ===== HERO FULL-BLEED BACKGROUND SLIDER ===== */}
      <section className="relative h-[82vh] md:h-[88vh] w-full">
        <HeroBackground images={heroImages} showControls />
        <motion.div variants={heroText} initial="hidden" animate="show" className="relative z-10 h-full flex items-center">
          <div className="container">
            <div className="max-w-3xl">
              <p className="uppercase tracking-[0.3em] text-white/80 text-xs md:text-sm mb-3">
                Best Offers
              </p>
              <h1 className="text-5xl md:text-7xl font-extrabold leading-[1.05] text-white drop-shadow-[0_2px_8px_rgba(0,0,0,0.45)]">
                {t("hero.line2")}
              </h1>
              <p className="mt-4 md:mt-5 text-white/90 md:text-lg drop-shadow-[0_1px_6px_rgba(0,0,0,0.35)] max-w-2xl">
                {t("hero.desc")}
              </p>
              <div className="mt-6 flex items-center gap-3">
                <a href="#packages" className="btn btn-primary"> {t("hero.ctaExplore")} </a>
                <a href="/contact" className="btn btn-outline bg-white/5 hover:bg-white/10 text-white border-white/30">
                  {t("hero.ctaContact")}
                </a>
              </div>
            </div>
          </div>
        </motion.div>
      </section>

      {/* ===== PARALLAX x6 ===== */}
      <div className="container mt-10">
        <ParallaxStrip src="/1.png"  speed={80}><h3 className="text-2xl md:text-3xl font-bold">Nusa Penida West</h3></ParallaxStrip>
        <ParallaxStrip src="/2.png"  speed={60}><h3 className="text-2xl md:text-3xl font-bold">Cliff & Blue Lagoon</h3></ParallaxStrip>
        <ParallaxStrip src="/3.png"  speed={90}><h3 className="text-2xl md:text-3xl font-bold">Hidden Beaches</h3></ParallaxStrip>
        <ParallaxStrip src="/4.png"  speed={70}><h3 className="text-2xl md:text-3xl font-bold">Sunset Points</h3></ParallaxStrip>
        <ParallaxStrip src="/5.png"  speed={85}><h3 className="text-2xl md:text-3xl font-bold">Snorkeling Spots</h3></ParallaxStrip>
        <ParallaxStrip src="/6.png"  speed={65}><h3 className="text-2xl md:text-3xl font-bold">Island Vibes</h3></ParallaxStrip>
      </div>

      {/* ===== FEATURE STRIP (micro-interactions extra) ===== */}
      <section className="container mt-14">
        <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {[
            { title: "Private Tour", desc: "Schedule-friendly itineraries." },
            { title: "Local Guide", desc: "Friendly & pro photographers." },
            { title: "Easy Booking", desc: "Chat via WA / IG / Email." },
            { title: "Best Price", desc: "No hidden fees." }
          ].map((f, i) => (
            <motion.div
              key={i}
              className="card p-5 bg-white/70 dark:bg-slate-900/70 backdrop-blur hover:shadow-xl hover:-translate-y-0.5 transition"
              initial={{ opacity: 0, y: 12 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true, amount: 0.3 }}
              transition={{ duration: 0.35 }}
            >
              <h4 className="font-semibold mb-1">{f.title}</h4>
              <p className="text-sm text-slate-600 dark:text-slate-400">{f.desc}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* ===== GALLERY (lazy, masonry) ===== */}
      <section className="container mt-14">
        <h2 className="text-xl md:text-2xl font-bold mb-4 text-slate-900 dark:text-slate-100">
          {lang === "id" ? "Sorotan Destinasi" : lang === "ja" ? "おすすめスポット" : "Destination Highlights"}
        </h2>
        <div className="columns-1 sm:columns-2 lg:columns-3 gap-4 [column-fill:_balance]">
          {gallery.map((src, idx) => (
            <motion.img
              key={src}
              src={src}
              alt={`gallery-${idx + 1}`}
              loading="lazy"
              className="mb-4 w-full rounded-2xl shadow-smooth object-cover will-change-transform hover:scale-[1.01] transition"
              initial={{ opacity: 0, scale: 0.98, y: 8 }}
              whileInView={{ opacity: 1, scale: 1, y: 0 }}
              viewport={{ once: true, amount: 0.2 }}
              transition={{ duration: 0.35, ease: "easeOut" }}
            />
          ))}
        </div>
      </section>

      {/* ===== PACKAGES (tetap, dengan animasi halus) ===== */}
      <section id="packages" className="container mt-16">
        <div className="flex items-end justify-between mb-4">
          <h2 className="text-xl md:text-2xl font-bold text-slate-900 dark:text-slate-100">
            {LABELS[lang]?.popular || LABELS.en.popular}
          </h2>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500 dark:text-slate-400">
              {LABELS[lang]?.calcFor || LABELS.en.calcFor}
            </label>
            <select
              id="pax" value={pax} onChange={(e) => setPax(parseInt(e.target.value))}
              className="px-3 py-2 rounded-2xl"
            >
              {[1,2,3,4,5,6].map(n => <option key={n} value={n}>{n} pax</option>)}
            </select>
          </div>
        </div>

        <motion.div variants={listVariants} initial="hidden" animate="show" className="grid md:grid-cols-2 gap-4">
          {data.map(p => {
            const tier = p.priceTiers?.find(x => parseInt(x[0]) === pax) || p.priceTiers?.[0];
            const price = tier ? tier[1] : 0;
            const preview = (p.spots && L(p.spots)) || (p.spots_day1 && L(p.spots_day1)) || [];
            return (
              <motion.article
                key={p.id}
                variants={itemVariants}
                className="card p-4 group hover:shadow-xl transition will-change-transform"
                whileHover={{ y: -2 }}
              >
                <h3 className="font-semibold text-lg mb-1 text-slate-900 dark:text-slate-100">
                  {L(p.title)}
                </h3>
                <p className="text-sm text-slate-500 dark:text-slate-400 mb-2">
                  {preview.slice(0, 4).join(" • ")}...
                </p>
                <div className="flex items-center justify-between">
                  <p className="text-sky-600 dark:text-sky-400 font-bold">
                    {toIDR(price)}{" "}
                    <span className="text-sm font-normal text-slate-500 dark:text-slate-400">
                      {LABELS[lang]?.paxSuffix || LABELS.en.paxSuffix}
                    </span>
                  </p>
                  <button
                    className="btn btn-primary"
                    onClick={() => addItem({ id: p.id, title: L(p.title), price, qty: 1 })}
                  >
                    {LABELS[lang]?.addToCart || LABELS.en.addToCart}
                  </button>
                </div>
              </motion.article>
            );
          })}
        </motion.div>
      </section>
    </>
  );
}
