import React, { useMemo } from "react";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import rawData from "../data/packages.json";
import { toIDR } from "../utils/format";

const listVariants = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { staggerChildren: 0.06, delayChildren: 0.05 } },
};
const itemVariants = {
  hidden: { opacity: 0, y: 10 },
  show: { opacity: 1, y: 0, transition: { duration: 0.28, ease: "easeOut" } },
};

function useLangPick() {
  const { i18n } = useTranslation();
  const lang = (i18n.resolvedLanguage || i18n.language || "en").split("-")[0];
  const pick = (val) =>
    typeof val === "string" ? val : val?.[lang] ?? val?.en ?? val?.id ?? "";
  const pickArr = (val) =>
    Array.isArray(val) ? val : val?.[lang] ?? val?.en ?? val?.id ?? [];
  return { pick, pickArr };
}

export default function Explore() {
  const { t } = useTranslation();
  const { pick, pickArr } = useLangPick();

  const data = useMemo(
    () =>
      rawData.map((p) => ({
        ...p,
        _title: pick(p.title),
        _spots: pickArr(p.spots),
        _spots1: pickArr(p.spots_day1),
        _spots2: pickArr(p.spots_day2),
        _snorkel: pickArr(p.snorkeling),
        _include: pickArr(p.include),
        _note: pick(p.note ?? ""),
      })),
    [pick, pickArr]
  );

  return (
    <div className="container mt-6 space-y-4">
      <motion.h1
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.35, ease: "easeOut" }}
        className="text-2xl font-bold text-slate-900 dark:text-slate-100"
      >
        {t("explore.title")}
      </motion.h1>

      <motion.div variants={listVariants} initial="hidden" animate="show" className="grid md:grid-cols-2 gap-4">
        {data.map((p) => (
          <motion.div key={p.id} variants={itemVariants} className="card p-4">
            <details>
              <summary className="cursor-pointer font-semibold text-slate-900 dark:text-slate-100">
                {p._title}
              </summary>

              <div className="mt-3 grid md:grid-cols-2 gap-3">
                <div>
                  {p._spots.length > 0 && (
                    <>
                      <h4 className="font-medium">{t("explore.spots")}</h4>
                      <ul className="list-disc ml-5 text-sm">{p._spots.map((s, i) => <li key={i}>{s}</li>)}</ul>
                    </>
                  )}

                  {p._spots1.length > 0 && (
                    <>
                      <h4 className="font-medium">{t("explore.day1")}</h4>
                      <ul className="list-disc ml-5 text-sm">{p._spots1.map((s, i) => <li key={i}>{s}</li>)}</ul>
                      {p._spots2.length > 0 && (
                        <>
                          <h4 className="font-medium mt-2">{t("explore.day2")}</h4>
                          <ul className="list-disc ml-5 text-sm">{p._spots2.map((s, i) => <li key={i}>{s}</li>)}</ul>
                        </>
                      )}
                    </>
                  )}

                  {p._snorkel.length > 0 && (
                    <>
                      <h4 className="font-medium mt-2">{t("explore.snorkeling")}</h4>
                      <ul className="list-disc ml-5 text-sm">{p._snorkel.map((s, i) => <li key={i}>{s}</li>)}</ul>
                    </>
                  )}
                </div>

                <div>
                  {p._include.length > 0 && (
                    <>
                      <h4 className="font-medium">{t("explore.includes")}</h4>
                      <ul className="list-disc ml-5 text-sm">{p._include.map((s, i) => <li key={i}>{s}</li>)}</ul>
                    </>
                  )}

                  {p.priceTiers?.length > 0 && (
                    <>
                      <h4 className="font-medium mt-2">{t("explore.prices")}</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {p.priceTiers.map(([n, price]) => (
                          <li key={n}>
                            {n} {t("home.pax")}: {toIDR(price)}
                          </li>
                        ))}
                      </ul>
                    </>
                  )}

                  {p._note && <p className="text-sm text-slate-500 dark:text-slate-400 mt-2">{p._note}</p>}
                </div>
              </div>
            </details>
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
}
