// src/components/Navbar.jsx (potongan – ganti blok Mobile)
import React, { useEffect, useRef, useState } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import { Menu, Moon, Sun, Heart, Globe, LogOut, UserRound } from "lucide-react";
import { useTranslation } from "react-i18next";
import { useCart } from "../context/CartContext";
import { useAuth } from "../context/AuthContext";
import { supabase } from "../lib/supabaseClient.js";

const LANGS = [
  { code: "en", label: "English",  flag: "🇬🇧" },
  { code: "id", label: "Indonesia", flag: "🇮🇩" },
  { code: "ja", label: "日本語",     flag: "🇯🇵" },
];

export default function Navbar({ onCartOpen }) {
  const { theme, setTheme } = useTheme();
  const [open, setOpen] = useState(false);
  const [atTop, setAtTop] = useState(true);
  const [show, setShow] = useState(true);
  const lastY = useRef(0);

  const { t, i18n } = useTranslation();
  const { items } = useCart();
  const { role, session } = useAuth();
  const isAdmin = role === "admin";
  const wishlistCount = items.reduce((s, it) => s + (it.qty || 0), 0);
  const location = useLocation();

  useEffect(() => setOpen(false), [location.pathname]);
  useEffect(() => {
    const onScroll = () => {
      const y = window.scrollY || 0;
      setAtTop(y < 4);
      const goingDown = y > lastY.current + 2;
      const goingUp   = y < lastY.current - 2;
      if (goingDown) setShow(false);
      else if (goingUp) setShow(true);
      lastY.current = y;
    };
    window.addEventListener("scroll", onScroll, { passive: true });
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const changeLang = (code) => {
    i18n.changeLanguage(code);
    try { localStorage.setItem("i18nextLng", code); } catch {}
  };

  const navItem = (to, label) => (
    <NavLink
      to={to}
      className={({ isActive }) =>
        "px-3 py-2 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800 " +
        (isActive ? "bg-slate-100 dark:bg-slate-800" : "")
      }
      onClick={() => setOpen(false)}
    >
      {label}
    </NavLink>
  );

  const adminLabels = {
    dashboard: t("admin.menu.dashboard"),
    orders: t("admin.menu.orders"),
    customize: t("admin.menu.customize"),
  };

  return (
    <header className={`fixed top-0 left-0 right-0 z-40 transition-transform duration-300 ${show || atTop ? "translate-y-0" : "-translate-y-full"}`}>
      <div className="bg-white/80 dark:bg-slate-900/80 backdrop-blur border-b border-slate-200 dark:border-slate-800">
        <div className="container flex items-center justify-between h-16">
          <div className="flex items-center gap-3">
            <button className="md:hidden p-2" onClick={() => setOpen(v=>!v)} aria-label="Toggle menu">
              <Menu />
            </button>
            <Link to="/" className="relative h-12 md:h-14 w-[210px] select-none" aria-label="CIDIKA TRAVEL&TOUR">
              <img src="/biru.png" className="absolute inset-0 h-full w-auto opacity-100 dark:opacity-0 transition-opacity" alt="CIDIKA" />
              <img src="/putih.png" className="absolute inset-0 h-full w-auto opacity-0 dark:opacity-100 transition-opacity" alt="" />
            </Link>
          </div>

          {/* Desktop nav (tetap seperti punyamu) */}
          <nav className="hidden md:flex items-center gap-2">
            {!isAdmin ? (
              <>
                {navItem("/", t("nav.home", { defaultValue: "Home" }))}
                {navItem("/explore", t("nav.explore"))}
                {navItem("/destinasi", t("nav.destinasi"))}
                {navItem("/faq", t("nav.faq"))}
                {navItem("/contact", t("nav.contact"))}
              </>
            ) : (
              <>
                {navItem("/admin", adminLabels.dashboard)}
                {navItem("/admin/orderan", adminLabels.orders)}
                {navItem("/admin/kustomisasi", adminLabels.customize)}
              </>
            )}
            <button
              className="p-2 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800"
              onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
              aria-label="Toggle theme"
            >
              {theme === "dark" ? <Sun size={18}/> : <Moon size={18}/>}
            </button>
            {/* Desktop language pakai select juga (stabil) */}
            <div className="flex items-center gap-1">
              <Globe size={16} className="opacity-70" />
              <select
                className="px-2 py-1 rounded-xl border bg-white/90 dark:bg-slate-900/90"
                value={i18n.language?.slice(0,2) || "id"}
                onChange={(e)=>changeLang(e.target.value)}
              >
                {LANGS.map(l => <option key={l.code} value={l.code}>{l.flag} {l.label}</option>)}
              </select>
            </div>
            {!isAdmin && (
              <button onClick={onCartOpen} className="relative p-2 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800" aria-label="Wishlist">
                <Heart size={18}/>
                {wishlistCount>0 && <span className="absolute -top-1 -right-1 min-w-[18px] h-[18px] px-[6px] rounded-full bg-sky-600 text-white text-[10px] leading-[18px] text-center">{wishlistCount}</span>}
              </button>
            )}
            {!session ? (
              <NavLink to="/admin/login" className="p-2 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800" aria-label="Login"><UserRound size={18}/></NavLink>
            ) : (
              <button className="p-2 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800" onClick={async()=>{await supabase.auth.signOut(); window.location.href="/";}} aria-label="Logout"><LogOut size={18}/></button>
            )}
          </nav>
        </div>

        {/* MOBILE V1 */}
        {open && (
          <div className="md:hidden border-t border-slate-200 dark:border-slate-800 p-3 space-y-3">
            <div className="grid gap-2">
              {!isAdmin ? (
                <>
                  <Link to="/" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl bg-white/70 dark:bg-slate-900/70 hover:bg-white dark:hover:bg-slate-800">{t("nav.home", { defaultValue: "Home" })}</Link>
                  <Link to="/explore" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.explore")}</Link>
                  <Link to="/destinasi" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.destinasi")}</Link>
                  <Link to="/faq" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.faq")}</Link>
                  <Link to="/contact" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.contact")}</Link>
                </>
              ) : (
                <>
                  <Link to="/admin" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("admin.menu.dashboard")}</Link>
                  <Link to="/admin/orderan" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("admin.menu.orders")}</Link>
                  <Link to="/admin/kustomisasi" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("admin.menu.customize")}</Link>
                </>
              )}

              {/* Bar aksi */}
              <div className="grid grid-cols-2 gap-2">
                <button onClick={()=>setTheme(theme==="dark"?"light":"dark")} className="px-3 py-3 rounded-2xl border">{theme==="dark"?"☀️ Light":"🌙 Dark"}</button>

                {/* Inilah FIX bahasa: select native di mobile */}
                <div className="px-3 py-3 rounded-2xl border flex items-center gap-2">
                  <Globe size={16}/><span className="text-sm">{t("nav.language")}</span>
                  <select
                    className="ml-auto bg-transparent outline-none"
                    value={i18n.language?.slice(0,2) || "id"}
                    onChange={(e)=>changeLang(e.target.value)}
                  >
                    {LANGS.map(l => <option key={l.code} value={l.code}>{l.flag} {l.code.toUpperCase()}</option>)}
                  </select>
                </div>
              </div>

              {!isAdmin && (
                <button onClick={onCartOpen} className="px-3 py-3 rounded-2xl border relative text-left">
                  ❤️ Wishlist
                  {wishlistCount>0 && <span className="absolute top-2 right-3 min-w-[18px] h-[18px] px-[6px] rounded-full bg-sky-600 text-white text-[10px] leading-[18px] text-center">{wishlistCount}</span>}
                </button>
              )}

              {session ? (
                <button onClick={async()=>{await supabase.auth.signOut(); window.location.href="/";}} className="px-3 py-3 rounded-2xl border flex items-center gap-2"><LogOut size={16}/> Logout</button>
              ) : (
                <Link to="/admin/login" onClick={()=>setOpen(false)} className="px-3 py-3 rounded-2xl border flex items-center gap-2"><UserRound size={16}/> Login</Link>
              )}
            </div>
          </div>
        )}
      </div>
    </header>
  );
}
