import React, { useEffect, useMemo, useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import { ChevronLeft, ChevronRight } from "lucide-react";

/**
 * Full-bleed background slider dengan control & auto-play.
 * - images: ["/hero1.png", ...]
 * - interval: ms auto-slide
 * - showControls: tampilkan tombol prev/next & dots
 */
export default function HeroBackground({
  images = [],
  interval = 4000,
  showControls = true,
  overlay = "from-black/50 via-black/20 to-transparent"
}) {
  const safeImages = useMemo(() => images.filter(Boolean), [images]);
  const [index, setIndex] = useState(0);

  // autoplay
  useEffect(() => {
    if (safeImages.length <= 1) return;
    const id = setInterval(() => setIndex(i => (i + 1) % safeImages.length), interval);
    return () => clearInterval(id);
  }, [safeImages.length, interval]);

  const prev = () => setIndex(i => (i - 1 + safeImages.length) % safeImages.length);
  const next = () => setIndex(i => (i + 1) % safeImages.length);

  return (
    <div className="absolute inset-0">
      <AnimatePresence mode="wait" initial={false}>
        <motion.img
          key={safeImages[index] || "empty"}
          src={safeImages[index]}
          alt=""
          aria-hidden
          className="absolute inset-0 w-full h-full object-cover"
          initial={{ opacity: 0, scale: 1.06 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0.0, scale: 1.02 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
        />
      </AnimatePresence>

      {/* gradient supaya teks kebaca */}
      <div className={`absolute inset-0 bg-gradient-to-b ${overlay}`} />

      {showControls && safeImages.length > 1 && (
        <>
          <button
            aria-label="Prev"
            onClick={prev}
            className="absolute left-3 top-1/2 -translate-y-1/2 rounded-full bg-black/35 hover:bg-black/50 p-2 text-white backdrop-blur"
          >
            <ChevronLeft />
          </button>
          <button
            aria-label="Next"
            onClick={next}
            className="absolute right-3 top-1/2 -translate-y-1/2 rounded-full bg-black/35 hover:bg-black/50 p-2 text-white backdrop-blur"
          >
            <ChevronRight />
          </button>

          {/* dots */}
          <div className="absolute bottom-4 left-0 right-0 flex items-center justify-center gap-2">
            {safeImages.map((_, i) => (
              <button
                key={i}
                onClick={() => setIndex(i)}
                aria-label={`Go to slide ${i + 1}`}
                className={`h-2.5 w-2.5 rounded-full transition
                ${i === index ? "bg-white" : "bg-white/50 hover:bg-white/70"}`}
              />
            ))}
          </div>
        </>
      )}
    </div>
  );
}
