import React from "react";
import { motion } from "framer-motion";
import { useTranslation } from "react-i18next";
import data from "../data/packages.json";
import { toIDR } from "../utils/format";

const listVariants = { hidden: { opacity: 0 }, show: { opacity: 1, transition: { staggerChildren: 0.06, delayChildren: 0.05 } } };
const itemVariants = { hidden: { opacity: 0, y: 10 }, show: { opacity: 1, y: 0, transition: { duration: 0.28, ease: "easeOut" } } };

function pickLang(val, lang) {
  if (val == null) return val;
  if (typeof val === "string" || Array.isArray(val)) return val;
  return val[lang] ?? val.en ?? val.id ?? val.ja ?? val;
}

export default function Explore() {
  const { t, i18n } = useTranslation();
  const lang = (i18n.language || "en").split("-")[0];

  return (
    <div className="container mt-6 space-y-4">
      <motion.h1
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.35, ease: "easeOut" }}
        className="text-2xl font-bold text-slate-900 dark:text-slate-100"
      >
        {t("explore.title")}
      </motion.h1>

      <motion.div variants={listVariants} initial="hidden" animate="show" className="grid md:grid-cols-2 gap-4">
        {data.map(p => {
          const title = pickLang(p.title, lang);
          const spots = pickLang(p.spots, lang);
          const sd1 = pickLang(p.spots_day1, lang);
          const sd2 = pickLang(p.spots_day2, lang);
          const snork = pickLang(p.snorkeling, lang);
          const include = pickLang(p.include, lang);
          const note = pickLang(p.note, lang);

          return (
            <motion.div key={p.id} variants={itemVariants} className="card p-4">
              <details>
                <summary className="cursor-pointer font-semibold text-slate-900 dark:text-slate-100">{title}</summary>

                <div className="mt-3 grid md:grid-cols-2 gap-3">
                  <div>
                    {Array.isArray(spots) && (
                      <>
                        <h4 className="font-medium">{t("explore.spots")}</h4>
                        <ul className="list-disc ml-5 text-sm">{spots.map((s, i) => <li key={i}>{s}</li>)}</ul>
                      </>
                    )}

                    {Array.isArray(sd1) && (
                      <>
                        <h4 className="font-medium">{t("explore.day1")}</h4>
                        <ul className="list-disc ml-5 text-sm">{sd1.map((s, i) => <li key={i}>{s}</li>)}</ul>
                        {Array.isArray(sd2) && (
                          <>
                            <h4 className="font-medium mt-2">{t("explore.day2")}</h4>
                            <ul className="list-disc ml-5 text-sm">{sd2.map((s, i) => <li key={i}>{s}</li>)}</ul>
                          </>
                        )}
                      </>
                    )}

                    {Array.isArray(snork) && (
                      <>
                        <h4 className="font-medium mt-2">{t("explore.snorkSpots")}</h4>
                        <ul className="list-disc ml-5 text-sm">{snork.map((s, i) => <li key={i}>{s}</li>)}</ul>
                      </>
                    )}
                  </div>

                  <div>
                    {Array.isArray(include) && (
                      <>
                        <h4 className="font-medium">{t("explore.includes")}</h4>
                        <ul className="list-disc ml-5 text-sm">{include.map((s, i) => <li key={i}>{s}</li>)}</ul>
                      </>
                    )}

                    {Array.isArray(p.priceTiers) && (
                      <>
                        <h4 className="font-medium mt-2">{t("explore.pricePerPax")}</h4>
                        <ul className="list-disc ml-5 text-sm">
                          {p.priceTiers.map(([n, price]) => <li key={n}>{n} pax: {toIDR(price)}</li>)}
                        </ul>
                      </>
                    )}

                    {note && <p className="text-sm text-slate-500 dark:text-slate-400 mt-2">{note}</p>}
                  </div>
                </div>
              </details>
            </motion.div>
          );
        })}
      </motion.div>
    </div>
  );
}
