import React, { useEffect, useMemo, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { motion, useInView } from "framer-motion";
import { toIDR } from "../utils/format";
import { useCart } from "../context/CartContext";
import rawData from "../data/packages.json";

/** ===== util i18n pick dari packages.json ===== */
function useLangPick() {
  const { i18n } = useTranslation();
  const lang = (i18n.resolvedLanguage || i18n.language || "en").split("-")[0];
  const pick = (val) => (typeof val === "string" ? val : val?.[lang] ?? val?.en ?? val?.id ?? "");
  const pickArr = (val) => (Array.isArray(val) ? val : val?.[lang] ?? val?.en ?? val?.id ?? []);
  return { pick, pickArr };
}

/** ===== HERO creative slider + parallax ===== */
const heroImages = ["/hero1.jpg","/hero2.jpg","/hero3.jpg","/hero4.jpg","/hero5.jpg","/hero6.jpg"];

export default function Home() {
  const { t } = useTranslation();
  const { addItem } = useCart();
  const { pick, pickArr } = useLangPick();

  // data paket sudah dipilih bahasanya
  const data = useMemo(
    () =>
      rawData.map((p) => ({
        ...p,
        _title: pick(p.title),
        _spotsPreview:
          pickArr(p.spots).slice(0, 4) ||
          pickArr(p.spots_day1).slice(0, 4) ||
          []
      })),
    [pick, pickArr]
  );

  /** Slider state */
  const [idx, setIdx] = useState(0);
  const total = heroImages.length;
  useEffect(() => {
    const id = setInterval(() => setIdx((i) => (i + 1) % total), 4800);
    return () => clearInterval(id);
  }, [total]);
  const go = (dir) => setIdx((i) => (i + dir + total) % total);

  /** Mouse parallax (3 layers) */
  const [mouse, setMouse] = useState({ x: 0, y: 0 });
  const onMouseMove = (e) => {
    const r = e.currentTarget.getBoundingClientRect();
    const mx = (e.clientX - r.left) / r.width - 0.5;
    const my = (e.clientY - r.top) / r.height - 0.5;
    setMouse({ x: mx, y: my });
  };

  /** Scroll reveal helper */
  const Section = ({ children, className = "" }) => {
    const ref = useRef(null);
    const inView = useInView(ref, { margin: "-80px", once: true });
    return (
      <motion.section
        ref={ref}
        initial={{ opacity: 0, y: 24 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ duration: 0.5, ease: "easeOut" }}
        className={className}
      >
        {children}
      </motion.section>
    );
  };

  /** ====== RENDER ====== */
  const [pax, setPax] = useState(2);

  return (
    <>
      {/* ===== HERO full-bleed ===== */}
      <section
        onMouseMove={onMouseMove}
        className="relative h-[76vh] md:h-[86vh] overflow-hidden"
      >
        {/* Slides as background layers */}
        {heroImages.map((src, i) => (
          <div
            key={src}
            className={`absolute inset-0 bg-cover bg-center transition-opacity duration-700 will-change-transform ${i === idx ? "opacity-100" : "opacity-0"}`}
            style={{ backgroundImage: `url(${src})` }}
          />
        ))}

        {/* Overlay gradient & subtle noise */}
        <div className="absolute inset-0 bg-gradient-to-b from-slate-950/50 via-slate-900/10 to-white/70 dark:to-slate-950/80 pointer-events-none" />
        <div className="absolute inset-0 mix-blend-overlay opacity-20 pointer-events-none"
             style={{ backgroundImage: "radial-gradient(#fff 1px, transparent 1px)", backgroundSize: "3px 3px" }} />

        {/* Mouse parallax blobs (3) */}
        <motion.div
          className="absolute -top-20 -left-20 w-64 h-64 rounded-full blur-3xl bg-sky-400/30"
          animate={{ x: mouse.x * 40, y: mouse.y * 30 }}
          transition={{ type: "spring", stiffness: 60, damping: 20 }}
        />
        <motion.div
          className="absolute top-24 -right-12 w-56 h-56 rounded-full blur-3xl bg-fuchsia-400/30"
          animate={{ x: mouse.x * -50, y: mouse.y * 20 }}
          transition={{ type: "spring", stiffness: 60, damping: 20 }}
        />
        <motion.div
          className="absolute bottom-10 left-1/2 -z-[1] -translate-x-1/2 w-[44rem] h-[44rem] rounded-full blur-3xl bg-amber-300/20"
          animate={{ x: mouse.x * 20, y: mouse.y * -25 }}
          transition={{ type: "spring", stiffness: 60, damping: 20 }}
        />

        {/* Headline */}
        <div className="relative z-10 container h-full flex flex-col justify-center">
          <motion.p
            initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, ease: "easeOut" }}
            className="tracking-[0.3em] text-xs md:text-sm text-white/80"
          >
            {t("hero.tag", { defaultValue: "BEST OFFERS" })}
          </motion.p>

          <motion.h1
            initial={{ opacity: 0, y: 16 }} animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: "easeOut", delay: 0.05 }}
            className="mt-2 font-extrabold text-white drop-shadow-[0_8px_24px_rgba(0,0,0,.35)] leading-[1.05] text-4xl md:text-6xl"
          >
            {t("hero.line2")}
          </motion.h1>

          <motion.p
            initial={{ opacity: 0, y: 16 }} animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: "easeOut", delay: 0.1 }}
            className="mt-3 max-w-xl text-white/90"
          >
            {t("hero.desc")}
          </motion.p>

          <motion.div
            initial={{ opacity: 0, y: 16 }} animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: "easeOut", delay: 0.15 }}
            className="mt-5 flex gap-3"
          >
            <a href="#packages" className="btn btn-primary">{t("hero.ctaExplore")}</a>
            <a href="/contact" className="btn btn-outline">{t("hero.ctaContact")}</a>
          </motion.div>
        </div>

        {/* Controls */}
        <div className="absolute inset-x-0 bottom-4 z-10 flex items-center justify-between container">
          <div className="flex gap-2">
            <button onClick={() => go(-1)} className="btn btn-outline backdrop-blur bg-white/40 dark:bg-slate-900/40">
              ‹
            </button>
            <button onClick={() => go(1)} className="btn btn-outline backdrop-blur bg-white/40 dark:bg-slate-900/40">
              ›
            </button>
          </div>
          <div className="flex gap-1.5">
            {heroImages.map((_, i) => (
              <button
                key={i}
                onClick={() => setIdx(i)}
                className={`h-2.5 w-2.5 rounded-full ring-1 ring-white/60 transition ${i === idx ? "bg-white" : "bg-white/30"}`}
                aria-label={`slide ${i+1}`}
              />
            ))}
          </div>
        </div>
      </section>

      {/* ===== value props / konten panjang dgn selingan gambar ===== */}
      <Section className="container mt-14">
        <div className="grid md:grid-cols-2 gap-8 items-start">
          <div>
            <h2 className="text-2xl md:text-3xl font-bold">
              {t("home.aboutTitle", { defaultValue: "Jelajahi Nusa Penida dengan Nyaman" })}
            </h2>
            <p className="mt-3 text-slate-600 dark:text-slate-300">
              {t("home.aboutBody", { defaultValue:
              "CIDIKA TRAVEL&TOUR adalah agen perjalanan yang fokus pada pengalaman di Nusa Penida: tour darat, snorkeling, hingga paket menginap. Kami menggabungkan kenyamanan, kehangatan, dan profesionalisme. Pemesanan mudah via Email, Instagram, WhatsApp, atau telepon—semua transparan dan aman." })}
            </p>
            <ul className="mt-4 space-y-2 text-slate-700 dark:text-slate-300">
              <li>• {t("home.point1", { defaultValue: "Paket lengkap: One Day, Half Day, 1D1N + Snorkeling, 2D1N." })}</li>
              <li>• {t("home.point2", { defaultValue: "Guide lokal berpengalaman (bisa bantu dokumentasi foto)." })}</li>
              <li>• {t("home.point3", { defaultValue: "Private tour—jadwal fleksibel, tidak digabung rombongan." })}</li>
              <li>• {t("home.point4", { defaultValue: "Harga jelas, sudah termasuk tiket fast boat PP & tiket masuk." })}</li>
            </ul>
          </div>
          <motion.img
            initial={{ scale: .98, opacity: 0 }} whileInView={{ scale: 1, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: .6 }}
            src="/1.jpg"
            alt="Nusa Penida"
            className="rounded-2xl shadow-smooth object-cover w-full h-64 md:h-80"
            loading="lazy"
          />
        </div>
      </Section>

      <Section className="mt-16">
        <div className="relative h-[36vh] md:h-[44vh] overflow-hidden rounded-none">
          <div className="absolute inset-0 bg-cover bg-center bg-fixed" style={{ backgroundImage: "url(/7.jpg)" }} />
          <div className="absolute inset-0 bg-gradient-to-b from-black/40 to-black/30" />
          <div className="relative container h-full flex items-center">
            <h3 className="text-white text-2xl md:text-3xl font-bold drop-shadow">
              {t("home.banner1", { defaultValue: "Spot ikonik: Kelingking, Diamond, Crystal Bay—semuanya dalam satu perjalanan." })}
            </h3>
          </div>
        </div>
      </Section>

      <Section className="container mt-14">
        <div className="grid md:grid-cols-3 gap-4">
          {[
            { title: t("home.how1", { defaultValue: "Pilih Paket" }), desc: t("home.how1d", { defaultValue: "Lihat detail & itinerary yang sesuai kebutuhan." }), img: "/12.jpg" },
            { title: t("home.how2", { defaultValue: "Hubungi Kami" }), desc: t("home.how2d", { defaultValue: "Konfirmasi tanggal—kami cek ketersediaan cepat." }), img: "/18.jpg" },
            { title: t("home.how3", { defaultValue: "Berangkat!" }), desc: t("home.how3d", { defaultValue: "Nikmati perjalanan—kami urus logistik & kenyamanan." }), img: "/23.jpg" }
          ].map((c, i) => (
            <motion.article
              key={i}
              whileHover={{ y: -4 }}
              className="card overflow-hidden"
            >
              <img src={c.img} alt="" className="h-40 w-full object-cover" loading="lazy" />
              <div className="p-4">
                <h4 className="font-semibold">{c.title}</h4>
                <p className="text-sm text-slate-600 dark:text-slate-400">{c.desc}</p>
              </div>
            </motion.article>
          ))}
        </div>
      </Section>

      {/* ===== daftar paket populer (dinamis, multi-bahasa) ===== */}
      <Section id="packages" className="container mt-16">
        <div className="flex items-end justify-between mb-4">
          <h2 className="text-xl md:text-2xl font-bold">{t("home.popular", { defaultValue: "Paket Populer" })}</h2>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500 dark:text-slate-400">
              {t("home.calcFor", { defaultValue: "Hitung harga untuk" })}
            </label>
            <select id="pax" value={pax} onChange={(e)=>setPax(parseInt(e.target.value))} className="px-3 py-2 rounded-2xl">
              {[1,2,3,4,5,6].map(n => <option key={n} value={n}>{n} {t("home.pax", { defaultValue: "pax" })}</option>)}
            </select>
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          {data.map((p) => {
            const tier = p.priceTiers?.find(x => parseInt(x[0]) === pax) || p.priceTiers?.[0];
            const price = tier ? tier[1] : 0;
            return (
              <motion.article
                key={p.id}
                initial={{ opacity: 0, y: 14 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }}
                transition={{ duration: .35, ease: "easeOut" }}
                className="card p-4"
              >
                <h3 className="font-semibold text-lg mb-1">{p._title}</h3>
                <p className="text-sm text-slate-500 dark:text-slate-400 mb-2">{p._spotsPreview.join(" • ")}...</p>
                <div className="flex items-center justify-between">
                  <p className="text-sky-600 dark:text-sky-400 font-bold">
                    {toIDR(price)}{" "}
                    <span className="text-sm font-normal text-slate-500 dark:text-slate-400">
                      /{t("home.perPax", { defaultValue: "pax" })}
                    </span>
                  </p>
                  <button
                    className="btn btn-primary"
                    onClick={() => addItem({ id: p.id, title: p._title, price, qty: 1 })}
                  >
                    {t("home.addToCart", { defaultValue: "Tambah ke Keranjang" })}
                  </button>
                </div>
              </motion.article>
            );
          })}
        </div>
      </Section>

      {/* CTA kontak singkat */}
      <Section className="container mt-16 mb-20">
        <div className="card p-6 md:p-8 flex flex-col md:flex-row items-start md:items-center gap-5">
          <div className="flex-1">
            <h3 className="text-xl md:text-2xl font-bold">
              {t("home.ctaTitle", { defaultValue: "Siap Berpetualang di Nusa Penida?" })}
            </h3>
            <p className="text-slate-600 dark:text-slate-300">
              {t("home.ctaDesc", { defaultValue: "Hubungi kami via WhatsApp/Instagram/Email. Tim akan bantu atur itinerary terbaik sesuai waktu & budget." })}
            </p>
          </div>
          <div className="flex gap-3">
            <a href="https://wa.me/6289523949667" className="btn btn-primary" target="_blank" rel="noreferrer">WhatsApp</a>
            <a href="/contact" className="btn btn-outline">{t("hero.ctaContact")}</a>
          </div>
        </div>
      </Section>
    </>
  );
}
