import React, { useEffect, useRef, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import { Menu, Moon, Sun, ShoppingCart, Globe } from "lucide-react";
import { useTranslation } from "react-i18next";
import { useCart } from "../context/CartContext";

const FLAGS = { en: "ðŸ‡¬ðŸ‡§", id: "ðŸ‡®ðŸ‡©", ja: "ðŸ‡¯ðŸ‡µ" };

export default function Navbar({ onCartOpen }) {
  const { theme, setTheme } = useTheme();
  const [open, setOpen] = useState(false);
  const [langOpen, setLangOpen] = useState(false);
  const langRef = useRef(null);
  const { t, i18n } = useTranslation();
  const { items } = useCart();

  const totalQty = items.reduce((n, it) => n + (it.qty || 0), 0);
  const currentLang = (i18n.language || "en").split("-")[0];

  useEffect(() => {
    function onClickOutside(e) {
      if (langRef.current && !langRef.current.contains(e.target)) setLangOpen(false);
    }
    window.addEventListener("click", onClickOutside);
    return () => window.removeEventListener("click", onClickOutside);
  }, []);

  const navItem = (to, label) => (
    <NavLink
      to={to}
      className={({ isActive }) =>
        "px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800 " +
        (isActive ? "bg-slate-100 dark:bg-slate-800" : "")
      }
      onClick={() => setOpen(false)}
    >
      {label}
    </NavLink>
  );

  const LangRow = ({ code, label }) => (
    <button
      onClick={() => { i18n.changeLanguage(code); setLangOpen(false); }}
      className="px-3 py-2 w-full text-left hover:bg-slate-100 dark:hover:bg-slate-800 flex items-center gap-2"
    >
      <span className="text-base leading-none">{FLAGS[code]}</span>
      <span>{label}</span>
    </button>
  );

  return (
    <header className="sticky top-0 z-40 bg-white/80 dark:bg-slate-900/80 backdrop-blur border-b border-slate-200 dark:border-slate-800">
      <div className="container flex items-center justify-between h-16">
        <div className="flex items-center gap-3">
          <button className="md:hidden p-2" onClick={() => setOpen(!open)} aria-label="Toggle menu"><Menu /></button>
          <Link to="/" className="font-bold text-xl tracking-wide">CIDIKA <span className="text-sky-600">TRAVEL&TOUR</span></Link>
        </div>

        <nav className="hidden md:flex items-center gap-2">
          {navItem("/explore", t("nav.explore"))}
          {navItem("/destinasi", t("nav.destinasi"))}
          {navItem("/faq", t("nav.faq"))}
          {navItem("/contact", t("nav.contact"))}
          <NavLink to="/admin/login" className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">
            {t("nav.login")}
          </NavLink>

          {/* Theme toggle */}
          <button
            className="p-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800"
            onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
            aria-label="Toggle theme"
          >
            {theme === "dark" ? <Sun size={18} /> : <Moon size={18} />}
          </button>

          {/* Language (klik, bukan hover) */}
          <div className="relative" ref={langRef}>
            <button
              className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800 flex items-center gap-2"
              aria-label="Language"
              onClick={() => setLangOpen((v) => !v)}
            >
              <Globe size={18} />
              <span className="text-sm">{FLAGS[currentLang]} {currentLang.toUpperCase()}</span>
            </button>
            {langOpen && (
              <div className="absolute right-0 mt-2 bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 rounded-xl overflow-hidden w-40 shadow-smooth">
                <LangRow code="en" label="English" />
                <LangRow code="id" label="Bahasa Indonesia" />
                <LangRow code="ja" label="æ—¥æœ¬èªž" />
              </div>
            )}
          </div>

          {/* Cart with badge */}
          <button onClick={onCartOpen} className="relative p-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800" aria-label="Cart">
            <ShoppingCart size={18} />
            {totalQty > 0 && (
              <span className="absolute -top-1 -right-1 bg-sky-600 text-white text-[10px] min-w-[18px] h-[18px] rounded-full flex items-center justify-center px-1">
                {totalQty}
              </span>
            )}
          </button>
        </nav>
      </div>

      {/* Mobile menu */}
      {open && (
        <div className="md:hidden border-t border-slate-200 dark:border-slate-800 p-2 space-y-2">
          <div className="flex flex-col">
            <Link to="/explore" onClick={() => setOpen(false)} className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.explore")}</Link>
            <Link to="/destinasi" onClick={() => setOpen(false)} className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.destinasi")}</Link>
            <Link to="/faq" onClick={() => setOpen(false)} className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.faq")}</Link>
            <Link to="/contact" onClick={() => setOpen(false)} className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.contact")}</Link>
            <Link to="/admin/login" onClick={() => setOpen(false)} className="px-3 py-2 rounded-xl hover:bg-slate-100 dark:hover:bg-slate-800">{t("nav.login")}</Link>

            <div className="flex items-center gap-2 px-3">
              <button onClick={() => setTheme(theme === "dark" ? "light" : "dark")} className="btn btn-outline">
                {theme === "dark" ? "Light" : "Dark"}
              </button>

              {/* Mobile language quick buttons */}
              <button onClick={() => { i18n.changeLanguage("en"); setOpen(false); }} className="btn btn-outline flex items-center gap-1">
                <span>ðŸ‡¬ðŸ‡§</span> EN
              </button>
              <button onClick={() => { i18n.changeLanguage("id"); setOpen(false); }} className="btn btn-outline flex items-center gap-1">
                <span>ðŸ‡®ðŸ‡©</span> ID
              </button>
              <button onClick={() => { i18n.changeLanguage("ja"); setOpen(false); }} className="btn btn-outline flex items-center gap-1">
                <span>ðŸ‡¯ðŸ‡µ</span> JA
              </button>

              <button onClick={onCartOpen} className="btn btn-outline">
                Cart{totalQty > 0 ? ` (${totalQty})` : ""}
              </button>
            </div>
          </div>
        </div>
      )}
    </header>
  );
}
