import React, { useEffect, useMemo, useRef, useState, useCallback } from "react";
import { useTranslation } from "react-i18next";
import { Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import {
  MapPin,
  Calendar,
  Users,
  BadgeCheck,
  MessageCircle,
  Star,
  ChevronRight,
  ArrowRight,
  Search,
  Phone,
} from "lucide-react";
import usePackages from "../hooks/usePackages";
import { useCurrency } from "../context/CurrencyContext";
import { formatMoneyFromIDR } from "../utils/currency";

/* ===========================================================
   KONFIG & DATA â€” hero & fallback
=========================================================== */
const heroImages = [
  "/hero1.jpg",
  "/hero2.jpg",
  "/hero3.jpeg",
  "/hero4.jpg",
  "/hero5.jpg",
  "/hero6.jpeg",
];

/* ===========================================================
   UTILS & HOOKS
=========================================================== */
// 1) Preload hero agar crossfade mulus (Efek #1)
function usePreload(images) {
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, [images]);
}

// 2) Ambil gambar paket dari berbagai field
function getPkgImage(p) {
  const raw =
    p?.default_image ||
    p?.cover_url ||
    p?.thumbnail ||
    p?.thumb_url ||
    p?.image_url ||
    (Array.isArray(p?.images) && p.images[0]) ||
    (p?.data?.images && p.data.images[0]) ||
    "";
  if (!raw) return "/23.jpg";
  if (/^https?:\/\//i.test(raw)) return raw;
  return raw.startsWith("/") ? raw : `/${raw}`;
}

// 3) Reduced motion
function usePrefersReducedMotion() {
  const [reduced, setReduced] = useState(false);
  useEffect(() => {
    const m = window.matchMedia?.("(prefers-reduced-motion: reduce)");
    if (!m) return;
    setReduced(!!m.matches);
    const onChange = () => setReduced(!!m.matches);
    m.addEventListener?.("change", onChange);
    return () => m.removeEventListener?.("change", onChange);
  }, []);
  return reduced;
}

// 4) Count up angka (Efek #2)
function useCountUp({ from = 0, to = 100, duration = 1200, start = false }) {
  const [value, setValue] = useState(from);
  const rafRef = useRef();
  useEffect(() => {
    if (!start) return;
    const t0 = performance.now();
    const step = (t) => {
      const p = Math.min(1, (t - t0) / duration);
      setValue(Math.floor(from + (to - from) * p));
      if (p < 1) rafRef.current = requestAnimationFrame(step);
    };
    rafRef.current = requestAnimationFrame(step);
    return () => cancelAnimationFrame(rafRef.current);
  }, [from, to, duration, start]);
  return value;
}

// 5) Spotlight hover (Efek #3)
function useSpotlight() {
  const ref = useRef(null);
  const onMove = useCallback((e) => {
    const el = ref.current;
    if (!el) return;
    const r = el.getBoundingClientRect();
    const x = e.clientX - r.left;
    const y = e.clientY - r.top;
    el.style.setProperty("--sx", `${x}px`);
    el.style.setProperty("--sy", `${y}px`);
  }, []);
  const onLeave = useCallback(() => {
    const el = ref.current;
    if (!el) return;
    el.style.setProperty("--sx", `-100px`);
    el.style.setProperty("--sy", `-100px`);
  }, []);
  return { ref, onMove, onLeave };
}

// 6) Tilt ringan (Efek #4)
function useTilt(max = 6) {
  const ref = useRef(null);
  useEffect(() => {
    const el = ref.current;
    if (!el) return;
    let raf = 0;
    const onMove = (e) => {
      const r = el.getBoundingClientRect();
      const px = (e.clientX - r.left) / r.width - 0.5;
      const py = (e.clientY - r.top) / r.height - 0.5;
      cancelAnimationFrame(raf);
      raf = requestAnimationFrame(() => {
        el.style.transform = `perspective(1000px) rotateX(${(-py * max).toFixed(2)}deg) rotateY(${(px * max).toFixed(
          2
        )}deg)`;
      });
    };
    const reset = () => {
      cancelAnimationFrame(raf);
      el.style.transform = "perspective(1000px) rotateX(0deg) rotateY(0deg)";
    };
    el.addEventListener("mousemove", onMove);
    el.addEventListener("mouseleave", reset);
    return () => {
      el.removeEventListener("mousemove", onMove);
      el.removeEventListener("mouseleave", reset);
      cancelAnimationFrame(raf);
    };
  }, [max]);
  return ref;
}

/* ===========================================================
   MICRO VARIANTS (Efek #5: scroll-reveal & micro-interaction)
=========================================================== */
const reveal = {
  hidden: { opacity: 0, y: 14 },
  show: { opacity: 1, y: 0, transition: { duration: 0.55, ease: "easeOut" } },
};
const stagger = {
  hidden: {},
  show: { transition: { staggerChildren: 0.08, delayChildren: 0.05 } },
};

/* ===========================================================
   KOMPONEN: Hero Section (Efek #6: ken-burns + crossfade)
=========================================================== */
function Hero({ onSearch }) {
  const reduced = usePrefersReducedMotion();
  const [idx, setIdx] = useState(0);
  usePreload(heroImages);

  useEffect(() => {
    if (reduced) return;
    const id = setInterval(() => setIdx((i) => (i + 1) % heroImages.length), 5200);
    return () => clearInterval(id);
  }, [reduced]);

  return (
    <section className="relative h-[78vh] md:h-[88vh] overflow-hidden grain">
      {/* Background slideshow */}
      {heroImages.map((src, i) => (
        <img
          key={src}
          src={src}
          alt=""
          className={`absolute inset-0 w-full h-full object-cover transition-opacity duration-[1200ms] ${
            i === idx ? "opacity-100 kenburns" : "opacity-0"
          }`}
          decoding="async"
          loading={i === 0 ? "eager" : "lazy"}
          fetchpriority={i === 0 ? "high" : "auto"}
        />
      ))}
      {/* Gradient overlay agar teks terbaca */}
      <div className="absolute inset-0 bg-gradient-to-b from-slate-950/70 via-slate-900/20 to-white/75 dark:to-slate-950/85" />

      {/* Floating orbs parallax ringan (Efek #7) */}
      <div className="absolute -top-24 -left-24 w-80 h-80 rounded-full blur-3xl bg-sky-400/30" />
      <div className="absolute top-24 -right-16 w-72 h-72 rounded-full blur-3xl bg-fuchsia-400/30" />
      <div className="absolute bottom-10 left-1/2 -translate-x-1/2 w-[48rem] h-[48rem] rounded-full blur-3xl bg-amber-300/20 -z-[1]" />

      {/* Copy */}
      <div className="relative z-10 container h-full flex flex-col justify-center">
        <motion.p
          variants={reveal}
          initial="hidden"
          animate="show"
          className="tracking-[0.28em] text-xs md:text-sm text-white/80"
        >
          NUSA PENIDA â€¢ OPEN TRIP
        </motion.p>

        <motion.h1
          variants={reveal}
          initial="hidden"
          animate="show"
          className="mt-2 font-extrabold text-white leading-[1.05] text-4xl md:text-6xl drop-shadow-[0_8px_24px_rgba(0,0,0,.35)]"
        >
          Jelajah Nusa Penida <span className="text-sky-300">lebih mudah & hemat</span>
        </motion.h1>

        <motion.p
          variants={reveal}
          initial="hidden"
          animate="show"
          className="mt-3 max-w-xl text-white/90"
        >
          Lihat paket populer, cek itinerary, dan pesan langsung via WhatsApp
          tanpa login. Cocok untuk solo, couple, hingga rombongan!
        </motion.p>

        {/* Quick search / intent chips (Efek #8: spring-in) */}
        <motion.div
          variants={stagger}
          initial="hidden"
          animate="show"
          className="mt-6 flex flex-wrap items-center gap-2"
        >
          {[
            { label: "One Day Trip", q: "one-day" },
            { label: "2D1N", q: "2d1n" },
            { label: "3D2N", q: "3d2n" },
            { label: "Private", q: "private" },
          ].map(({ label, q }) => (
            <motion.button
              key={q}
              variants={reveal}
              onClick={() => onSearch?.(q)}
              className="btn glass !py-2 !px-3 text-sm"
            >
              <Search size={14} className="mr-1.5" />
              {label}
            </motion.button>
          ))}
        </motion.div>

        <motion.div variants={reveal} initial="hidden" animate="show" className="mt-5 flex gap-3">
          <a href="#popular" className="btn btn-primary glass">Lihat Paket Populer</a>
          <Link to="/contact" className="btn btn-outline glass">Hubungi Kami</Link>
        </motion.div>
      </div>
    </section>
  );
}

/* ===========================================================
   REUSABLE: SpotlightCard wrapper (pakai useSpotlight)
=========================================================== */
function SpotlightCard({ glow = "rgba(14,165,233,.12)", radius = 300, className = "", children }) {
  const { ref, onMove, onLeave } = useSpotlight();
  return (
    <div
      ref={ref}
      onMouseMove={onMove}
      onMouseLeave={onLeave}
      className={`relative overflow-hidden rounded-2xl ${className}`}
    >
      <div
        className="pointer-events-none absolute inset-0 rounded-2xl"
        style={{
          background: `radial-gradient(${radius}px circle at var(--sx,-100px) var(--sy,-100px), ${glow}, transparent 60%)`,
          transition: "background .2s ease",
        }}
      />
      {children}
    </div>
  );
}

/* ===========================================================
   KOMPONEN: Feature list (Why Us)
=========================================================== */
function FeatureCard({ icon, title, text }) {
  return (
    <SpotlightCard className="card p-4">
      <div className="flex items-start gap-3 relative">
        <div className="shrink-0 p-2 rounded-xl bg-slate-100 dark:bg-slate-800">{icon}</div>
        <div>
          <div className="font-semibold">{title}</div>
          <p className="text-sm text-slate-600 dark:text-slate-300 mt-1">{text}</p>
        </div>
      </div>
    </SpotlightCard>
  );
}

function WhyUs() {
  const items = [
    {
      icon: <BadgeCheck className="text-emerald-500" size={18} />,
      title: "Operator Resmi & Berpengalaman",
      text: "Tim lokal Nusa Penida yang paham spot terbaik & waktu terbaik.",
    },
    {
      icon: <Users className="text-sky-500" size={18} />,
      title: "Cocok untuk Semua",
      text: "Solo, couple, family, atau rombongan kantorâ€”semua bisa diatur.",
    },
    {
      icon: <Calendar className="text-amber-500" size={18} />,
      title: "Jadwal Fleksibel",
      text: "Bisa pilih tanggal, private charter, atau gabung open trip.",
    },
    {
      icon: <MapPin className="text-fuchsia-500" size={18} />,
      title: "Itinerary Optimal",
      text: "Racikan rute hemat waktu, spot ramai tetap dapat momen terbaik.",
    },
  ];
  return (
    <section className="container mt-16">
      <motion.div variants={reveal} initial="hidden" whileInView="show" viewport={{ once: true }}>
        <h2 className="text-2xl md:text-3xl font-bold">Kenapa pilih kami?</h2>
        <p className="text-slate-600 dark:text-slate-300 mt-1">Keunggulan yang bikin trip kamu lebih tenang.</p>
      </motion.div>

      <motion.div
        variants={stagger}
        initial="hidden"
        whileInView="show"
        viewport={{ once: true, amount: 0.3 }}
        className="mt-6 grid sm:grid-cols-2 lg:grid-cols-4 gap-4"
      >
        {items.map((it, i) => (
          <motion.div key={i} variants={reveal}>
            <FeatureCard icon={it.icon} title={it.title} text={it.text} />
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
}

/* ===========================================================
   KOMPONEN: Stats dengan CountUp
=========================================================== */
function Stats() {
  const [start, setStart] = useState(false);
  const ref = useRef(null);
  useEffect(() => {
    const io = new IntersectionObserver(
      (e) => e.forEach((en) => en.isIntersecting && setStart(true)),
      { threshold: 0.4 }
    );
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);
  const trips = useCountUp({ to: 1200, duration: 1200, start });
  const photos = useCountUp({ to: 4800, duration: 1400, start });
  const rating = useCountUp({ to: 49, duration: 900, start }); // 4.9

  return (
    <section ref={ref} className="container mt-14">
      <div className="card p-5 md:p-7">
        <div className="grid sm:grid-cols-3 gap-4 text-center">
          <div>
            <div className="text-3xl font-extrabold">{trips.toLocaleString()}+</div>
            <div className="text-sm text-slate-500">Traveler puas</div>
          </div>
          <div>
            <div className="text-3xl font-extrabold">{photos.toLocaleString()}+</div>
            <div className="text-sm text-slate-500">Foto & video dokumentasi</div>
          </div>
          <div>
            <div className="text-3xl font-extrabold flex items-center justify-center">
              4.{Math.min(9, rating)} <Star size={18} className="ml-1 text-amber-500" />
            </div>
            <div className="text-sm text-slate-500">Rating Google & IG</div>
          </div>
        </div>
      </div>
    </section>
  );
}

/* ===========================================================
   Popular Card (pakai SpotlightCard)
=========================================================== */
function PopularCard({ pkg, price, pax, currency, fx, locale }) {
  const navigate = useNavigate();
  const cover = getPkgImage(pkg);
  const title = pkg?.locale?.title || pkg.slug || "Open Trip";
  const spots = (pkg?.locale?.spots || []).slice(0, 4).join(" â€¢ ");
  const priceLabel = formatMoneyFromIDR(price, currency, fx, locale);

  return (
    <SpotlightCard className="border border-slate-200 dark:border-slate-800 bg-white/90 dark:bg-slate-900/90 shadow-smooth">
      {/* image */}
      <div className="relative aspect-[16/10] overflow-hidden will-change-transform rounded-t-2xl">
        <img
          src={cover}
          alt=""
          loading="lazy"
          className="absolute inset-0 w-full h-full object-cover transition-transform duration-700 hover:scale-[1.04]"
        />
        <div className="absolute inset-x-0 bottom-0 h-28 bg-gradient-to-t from-black/65 to-transparent rounded-b-2xl" />
        <div className="absolute left-3 bottom-3 right-3 text-white drop-shadow">
          <h3 className="font-semibold text-lg line-clamp-1">{title}</h3>
          <p className="text-[13px] opacity-90 line-clamp-1">{spots}</p>
        </div>
      </div>

      {/* content */}
      <div className="p-4">
        <div className="flex items-center justify-between">
          <div className="text-sky-700 dark:text-sky-300 font-extrabold">
            {priceLabel}{" "}
            <span className="text-sm font-normal text-slate-600 dark:text-slate-400">/ pax</span>
          </div>
          <div className="text-xs text-slate-500 flex items-center gap-1">
            <Calendar size={14} /> Flexible
          </div>
        </div>

        <div className="mt-3 flex gap-2">
          <button
            className="btn glass"
            onClick={() =>
              navigate(`/explore?pkg=${pkg.id}`, { state: { openId: pkg.id, pax } })
            }
          >
            Lihat Detail <ChevronRight size={16} className="ml-1" />
          </button>
          <a
            href={`https://wa.me/6289523949667?text=Halo%20Admin,%20saya%20minat%20paket%20${encodeURIComponent(
              title
            )}%20untuk%20${pax}%20pax`}
            target="_blank"
            rel="noreferrer"
            className="btn btn-primary glass"
          >
            Pesan via WA <ArrowRight size={16} className="ml-1" />
          </a>
        </div>
      </div>
    </SpotlightCard>
  );
}

/* ===========================================================
   KOMPONEN: Paket Populer (Efek #9)
=========================================================== */
function PopularPackages({ data, currency, fx, locale }) {
  const [pax, setPax] = useState(1);

  // pilih 6 termurah sebagai "populer" (fallback)
  const items = useMemo(() => {
    const priced = (data || []).map((p) => {
      const t =
        (p.price_tiers || []).find((x) => x.pax === pax) ||
        (p.price_tiers || [])[0];
      return { p, price: t?.price_idr || 0 };
    });
    return priced.sort((a, b) => a.price - b.price).slice(0, 6);
  }, [data, pax]);

  return (
    <section id="popular" className="container mt-16">
      <motion.div variants={reveal} initial="hidden" whileInView="show" viewport={{ once: true }}>
        <div className="flex items-end justify-between">
          <div>
            <h2 className="text-2xl md:text-3xl font-bold">Paket Populer</h2>
            <p className="text-slate-600 dark:text-slate-300 mt-1">
              Pilihan terbaik wisatawan untuk Nusa Penida.
            </p>
          </div>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500">Hitung untuk</label>
            <select
              id="pax"
              value={pax}
              onChange={(e) => setPax(parseInt(e.target.value))}
              className="px-3 py-2 rounded-2xl"
            >
              {[1, 2, 3, 4, 5, 6].map((n) => (
                <option key={n} value={n}>
                  {n} pax
                </option>
              ))}
            </select>
          </div>
        </div>
      </motion.div>

      <motion.div
        variants={stagger}
        initial="hidden"
        whileInView="show"
        viewport={{ once: true, amount: 0.2 }}
        className="mt-5 grid sm:grid-cols-2 lg:grid-cols-3 gap-4"
      >
        {items.map(({ p, price }) => (
          <motion.div key={p.id} variants={reveal}>
            <PopularCard
              pkg={p}
              price={price}
              pax={pax}
              currency={currency}
              fx={fx}
              locale={locale}
            />
          </motion.div>
        ))}
      </motion.div>

      <div className="mt-5 text-right">
        <Link to="/explore" className="inline-flex items-center gap-2 text-sky-600 dark:text-sky-300 hover:underline">
          Lihat semua paket <ChevronRight size={16} />
        </Link>
      </div>
    </section>
  );
}

/* ===========================================================
   KOMPONEN: How It Works (alur utama)
=========================================================== */
function StepCard({ icon, title, text }) {
  const tilt = useTilt(4);
  return (
    <div ref={tilt} className="card p-4 flex items-start gap-3">
      <div className="p-2 rounded-xl bg-slate-100 dark:bg-slate-800">{icon}</div>
      <div>
        <div className="font-semibold">{title}</div>
        <p className="text-sm text-slate-600 dark:text-slate-300">{text}</p>
      </div>
    </div>
  );
}

function HowItWorks() {
  const steps = [
    {
      icon: <Search size={18} />,
      title: "Pilih Paket",
      text: "Bandingkan paket populer & sesuaikan pax.",
    },
    {
      icon: <MessageCircle size={18} />,
      title: "Chat Admin",
      text: "Klik tombol WhatsApp, kami balas cepat.",
    },
    {
      icon: <Calendar size={18} />,
      title: "Atur Jadwal",
      text: "Tetapkan tanggal & meeting point.",
    },
    {
      icon: <BadgeCheck size={18} />,
      title: "Berangkat!",
      text: "Nikmati tripâ€”dokumetasi tersedia.",
    },
  ];
  return (
    <section className="container mt-16">
      <motion.div variants={reveal} initial="hidden" whileInView="show" viewport={{ once: true }}>
        <h2 className="text-2xl md:text-3xl font-bold">Cara Kerja</h2>
        <p className="text-slate-600 dark:text-slate-300 mt-1">Simple dan cepat tanpa login.</p>
      </motion.div>
      <motion.div
        variants={stagger}
        initial="hidden"
        whileInView="show"
        viewport={{ once: true, amount: 0.3 }}
        className="mt-6 grid sm:grid-cols-2 lg:grid-cols-4 gap-4"
      >
        {steps.map((s, i) => (
          <motion.div key={i} variants={reveal}>
            <StepCard icon={s.icon} title={s.title} text={s.text} />
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
}

/* ===========================================================
   KOMPONEN: Testimonial singkat (Efek #10 auto-snap)
=========================================================== */
function Testimonials() {
  const data = [
    { name: "Laras", text: "Timnya ramah, itinerary pas, spot foto banyak!", city: "Jakarta" },
    { name: "Andi", text: "Private trip nyaman, driver ontime dan helpful.", city: "Bandung" },
    { name: "Yuki", text: "Suka banget sama snorkel & manta pointnya!", city: "Tokyo" },
  ];
  return (
    <section className="container mt-16">
      <motion.h2 variants={reveal} initial="hidden" whileInView="show" viewport={{ once: true }} className="text-2xl md:text-3xl font-bold">
        Kata Mereka
      </motion.h2>
      <div className="mt-4 overflow-x-auto snap-x">
        <div className="flex gap-4 min-w-max">
          {data.map((t, i) => (
            <motion.blockquote
              key={i}
              variants={reveal}
              initial="hidden"
              whileInView="show"
              viewport={{ once: true, amount: 0.3 }}
              className="snap-start w-[320px] card p-4"
            >
              <div className="flex items-center gap-2 text-amber-500 mb-1">
                {[...Array(5)].map((_, s) => <Star key={s} size={16} />)}
              </div>
              <p className="text-slate-700 dark:text-slate-200">{t.text}</p>
              <footer className="mt-3 text-sm text-slate-500">â€” {t.name}, {t.city}</footer>
            </motion.blockquote>
          ))}
        </div>
      </div>
    </section>
  );
}

