/* ===========================================================
   KOMPONEN: Sticky mini-CTA saat scroll (UX helper)
=========================================================== */
function StickyHelpCTA() {
  const [show, setShow] = useState(false);
  useEffect(() => {
    const onScroll = () => setShow((window.scrollY || 0) > 520);
    window.addEventListener("scroll", onScroll, { passive: true });
    onScroll();
    return () => window.removeEventListener("scroll", onScroll);
  }, []);
  return (
    <AnimatePresence>
      {show && (
        <motion.div
          initial={{ y: 40, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          exit={{ y: 40, opacity: 0 }}
          transition={{ type: "spring", stiffness: 320, damping: 26 }}
          className="fixed bottom-4 inset-x-0 z-30"
        >
          <div className="container">
            <a
              href="https://wa.me/6289523949667"
              target="_blank"
              rel="noreferrer"
              className="w-full sm:w-auto btn btn-primary glass flex items-center gap-2 shadow-smooth"
            >
              <MessageCircle size={16} /> Butuh bantuan? Chat WhatsApp
            </a>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

