export default function Home() {
  const { rows: data = [] } = usePackages();
  const { fx, currency, locale } = useCurrency();
  const navigate = useNavigate();

  const onQuickSearch = (q) => {
    navigate(`/explore?tag=${encodeURIComponent(q)}`);
  };

  return (
    <>
      <Hero onSearch={onQuickSearch} />
      <WhyUs />
      <Stats />
      <PopularPackages data={data} currency={currency} fx={fx} locale={locale} />
      <HowItWorks />
      <Testimonials />
      <BigCTA />
      <StickyHelpCTA />
    </>
  );
}
