import React, { useEffect, useMemo, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { motion, AnimatePresence, useScroll, useTransform } from "framer-motion";
import { toIDR } from "../utils/format";
import { useCart } from "../context/CartContext";
import rawData from "../data/packages.json";
import { Compass, Ship, Languages, ShieldCheck } from "lucide-react";

const heroImages = Array.from({ length: 6 }, (_, i) => `/hero${i + 1}.jpg`);

/* ===== helpers i18n untuk data paket ===== */
function useLangPick() {
  const { i18n } = useTranslation();
  const lang = (i18n.resolvedLanguage || i18n.language || "en").split("-")[0];
  const pick = (val) => (typeof val === "string" ? val : val?.[lang] ?? val?.en ?? val?.id ?? "");
  const pickArr = (val) => (Array.isArray(val) ? val : val?.[lang] ?? val?.en ?? val?.id ?? []);
  return { pick, pickArr };
}

/* ===== Parallax band reusable ===== */
function ParallaxBand({ img, children, speed = 0.2 }) {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref, offset: ["start end", "end start"] });
  const y = useTransform(scrollYProgress, [0, 1], [-(speed * 80), speed * 80]);

  return (
    <section ref={ref} className="relative h-[50vh] md:h-[60vh] my-10 rounded-3xl overflow-hidden">
      <motion.div
        style={{ y, backgroundImage: `url(${img})` }}
        className="absolute inset-0 will-change-transform bg-cover bg-center"
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-black/20 to-transparent" />
      <div className="relative h-full flex items-end">
        <div className="container pb-8">
          {children}
        </div>
      </div>
    </section>
  );
}

export default function Home() {
  const { t } = useTranslation();
  const { addItem } = useCart();
  const [pax, setPax] = useState(2);
  const { pick, pickArr } = useLangPick();

  // data paket sesuai bahasa
  const data = useMemo(
    () =>
      rawData.map((p) => ({
        ...p,
        _title: pick(p.title),
        _spots: pickArr(p.spots) || pickArr(p.spots_day1)?.concat(pickArr(p.spots_day2)) || [],
        _include: pickArr(p.include),
        _note: pick(p.note ?? "")
      })),
    [pick, pickArr]
  );

  /* ===== HERO slider state ===== */
  const [idx, setIdx] = useState(0);
  const timerRef = useRef(null);
  useEffect(() => {
    timerRef.current = setInterval(() => setIdx((i) => (i + 1) % heroImages.length), 3000);
    return () => clearInterval(timerRef.current);
  }, []);
  const go = (dir) => {
    clearInterval(timerRef.current);
    setIdx((i) => (i + dir + heroImages.length) % heroImages.length);
    timerRef.current = setInterval(() => setIdx((i) => (i + 1) % heroImages.length), 3000);
  };

  /* mouse parallax di hero */
  const [mxy, setMxy] = useState({ x: 0, y: 0 });
  const onMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setMxy({ x: e.clientX - rect.left - rect.width / 2, y: e.clientY - rect.top - rect.height / 2 });
  };

  return (
    <>
      {/* ===== HERO FULL BLEED ===== */}
      <section
        className="relative h-[72vh] md:h-[84vh] rounded-3xl overflow-hidden container mt-6"
        onMouseMove={onMouseMove}
      >
        <AnimatePresence mode="wait">
          <motion.div
            key={idx}
            className="absolute inset-0 bg-cover bg-center will-change-opacity will-change-transform"
            style={{ backgroundImage: `url(${heroImages[idx]})` }}
            initial={{ opacity: 0, scale: 1.03 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0.6 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
          />
        </AnimatePresence>
        {/* gradient readability */}
        <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,_rgba(0,0,0,0.2),_rgba(0,0,0,0.55))]" />
        <div className="absolute inset-0 bg-gradient-to-tr from-black/60 via-black/25 to-transparent" />

        {/* teks hero */}
        <div className="relative z-10 h-full flex items-center">
          <div className="max-w-2xl px-4">
            <p className="uppercase tracking-[0.3em] text-xs md:text-sm text-white/80 mb-2">
              {t("home.bestOffers", "BEST OFFERS")}
            </p>
            <h1 className="text-4xl md:text-6xl font-extrabold leading-tight text-white drop-shadow">
              {t("hero.line2")}<br />
              <span className="text-yellow-300">{t("home.taglineDays", "3 HARI")}</span>
            </h1>
            <p className="mt-3 text-white/90">
              {t("hero.desc")}
            </p>
            <div className="mt-5 flex gap-3">
              <a href="#packages" className="btn btn-primary">{t("hero.ctaExplore")}</a>
              <a href="/contact" className="btn btn-outline text-white border-white/40 hover:bg-white/10">
                {t("hero.ctaContact")}
              </a>
            </div>
          </div>
        </div>

        {/* tombol prev/next */}
        <button
          aria-label="Prev"
          onClick={() => go(-1)}
          className="absolute left-3 bottom-4 z-10 h-10 w-10 rounded-full bg-black/40 hover:bg-black/60 text-white grid place-items-center backdrop-blur"
        >
          ‹
        </button>
        <button
          aria-label="Next"
          onClick={() => go(1)}
          className="absolute left-16 bottom-4 z-10 h-10 w-10 rounded-full bg-black/40 hover:bg-black/60 text-white grid place-items-center backdrop-blur"
        >
          ›
        </button>

        {/* mouse-parallax dekor (3 layer) */}
        <motion.div
          className="absolute right-16 top-16 h-12 w-12 rounded-full bg-white/20"
          style={{ translateX: mxy.x * 0.02, translateY: mxy.y * 0.02 }}
        />
        <motion.div
          className="absolute right-32 top-28 h-6 w-6 rounded-full bg-sky-300/30"
          style={{ translateX: mxy.x * -0.03, translateY: mxy.y * 0.03 }}
        />
        <motion.div
          className="absolute right-24 top-40 h-8 w-8 rounded-full bg-amber-300/30"
          style={{ translateX: mxy.x * 0.05, translateY: mxy.y * -0.04 }}
        />
      </section>

      {/* ===== PARALLAX SECTIONS (4–6 buah) ===== */}
      <ParallaxBand img="/1.jpg" speed={0.25}>
        <h3 className="text-white text-2xl md:text-3xl font-bold drop-shadow mb-1">
          {t("home.aboutTitle", "Jelajahi Nusa Penida bersama CIDIKA TRAVEL&TOUR")}
        </h3>
        <p className="text-white/90 max-w-3xl">
          {t("home.aboutBody1",
          "CIDIKA menghadirkan perjalanan nyaman dan profesional: tour barat/timur, paket snorkeling, hingga paket menginap. Pemesanan mudah via Email, Instagram, WhatsApp, atau telepon.")}
        </p>
      </ParallaxBand>

      <section className="container grid md:grid-cols-3 gap-4 my-10">
        <motion.div whileHover={{ y: -6 }} className="card p-5">
          <div className="flex items-center gap-3 mb-2"><Compass /> <h4 className="font-semibold">Destinasi Ikonik</h4></div>
          <p className="text-sm text-slate-600 dark:text-slate-300">
            Kelingking, Diamond, Angel’s Billabong, Broken Beach, Crystal Bay—semua dirangkai efisien & nyaman.
          </p>
        </motion.div>
        <motion.div whileHover={{ y: -6 }} className="card p-5">
          <div className="flex items-center gap-3 mb-2"><Ship /> <h4 className="font-semibold">Snorkeling Seru</h4></div>
          <p className="text-sm text-slate-600 dark:text-slate-300">
            Manta Bay, Gamat, Wall Point. Termasuk sewa alat, jukung, dan pemandu snorkeling berpengalaman.
          </p>
        </motion.div>
        <motion.div whileHover={{ y: -6 }} className="card p-5">
          <div className="flex items-center gap-3 mb-2"><Languages /> <h4 className="font-semibold">3 Bahasa</h4></div>
          <p className="text-sm text-slate-600 dark:text-slate-300">
            Website default Inggris, serta Indonesia & Jepang. Admin bisa mengubah konten tiap halaman dari menu Kustomisasi.
          </p>
        </motion.div>
      </section>

      <ParallaxBand img="/7.jpg" speed={0.18}>
        <h3 className="text-white text-2xl md:text-3xl font-bold drop-shadow mb-1">
          {t("home.aboutTitle2", "Paket Lengkap: One Day, Half Day, 1D1N, 2D1N")}
        </h3>
        <p className="text-white/90 max-w-3xl">
          {t("home.aboutBody2",
          "Semua paket sudah termasuk tiket boat PP, transport AC + driver/guide, tiket masuk, dan makan siang.")}
        </p>
      </ParallaxBand>

      <section className="container my-10 grid md:grid-cols-2 gap-6">
        {/* mouse parallax kartu gambar */}
        <motion.div whileHover={{ scale: 1.02 }} className="card overflow-hidden">
          <motion.img
            src="/12.jpg"
            alt="Nusa Penida"
            className="w-full h-64 object-cover"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 120, damping: 15 }}
          />
          <div className="p-5">
            <h4 className="font-semibold mb-1">{t("home.bookingEasy", "Booking Mudah")}</h4>
            <p className="text-sm text-slate-600 dark:text-slate-300">
              {t("home.bookingBody",
              "Hubungi kami lewat Email, Instagram, WhatsApp, atau telepon. Tim akan membantu rekomendasi paket terbaik.")}
            </p>
          </div>
        </motion.div>

        <motion.div whileHover={{ scale: 1.02 }} className="card p-5">
          <div className="flex items-center gap-3 mb-2">
            <ShieldCheck /> <h4 className="font-semibold">{t("home.adminPanel", "Panel Admin & Supabase")}</h4>
          </div>
          <p className="text-sm text-slate-600 dark:text-slate-300">
            {t("home.adminBody",
            "Data tersimpan di Supabase. Admin (login) bisa mengelola konten multi-bahasa, foto, dan orderan. Pembeli bisa checkout sebagai tamu (anon).")}
          </p>
        </motion.div>
      </section>

      <ParallaxBand img="/19.jpg" speed={0.22}>
        <h3 className="text-white text-2xl md:text-3xl font-bold drop-shadow mb-1">
          {t("home.ctaSeePackages", "Siap Berpetualang? Lihat Paket Populer")}
        </h3>
        <p className="text-white/90">{t("home.ctaSeePackagesSub", "Pilih jumlah pax untuk melihat estimasi harga.")}</p>
      </ParallaxBand>

      {/* ===== PACKAGES (tetap ada) ===== */}
      <section id="packages" className="container mt-12">
        <div className="flex items-end justify-between mb-4">
          <h2 className="text-xl md:text-2xl font-bold text-slate-900 dark:text-slate-100">{t("home.popular", "Paket Populer")}</h2>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500 dark:text-slate-400">
              {t("home.calcFor", "Hitung harga untuk")}
            </label>
            <select
              id="pax"
              value={pax}
              onChange={(e) => setPax(parseInt(e.target.value))}
              className="px-3 py-2 rounded-2xl"
            >
              {[1, 2, 3, 4, 5, 6].map((n) => (
                <option key={n} value={n}>{n} {t("home.pax", "pax")}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          {data.map((p) => {
            const tier = p.priceTiers?.find((x) => parseInt(x[0]) === pax) || p.priceTiers?.[0];
            const price = tier ? tier[1] : 0;
            return (
              <article key={p.id} className="card p-4">
                <h3 className="font-semibold text-lg mb-1">{p._title}</h3>
                <p className="text-sm text-slate-500 mb-2">{p._spots.slice(0, 4).join(" • ")}...</p>
                <div className="flex items-center justify-between">
                  <p className="text-sky-600 font-bold">
                    {toIDR(price)} <span className="text-sm font-normal text-slate-500">/{t("home.perPax", "pax")}</span>
                  </p>
                  <button className="btn btn-primary" onClick={() => addItem({ id: p.id, title: p._title, price, qty: 1 })}>
                    {t("home.addToCart", "Tambah ke Keranjang")}
                  </button>
                </div>
              </article>
            );
          })}
        </div>
      </section>

      {/* parallax penutup (opsional ke-6) */}
      <ParallaxBand img="/23.jpg" speed={0.16}>
        <p className="text-white/90">
          {t("home.footerTease", "CIDIKA memadukan kenyamanan, kehangatan, dan profesionalisme untuk setiap perjalanan Anda.")}
        </p>
      </ParallaxBand>
    </>
  );
}
