import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";

/**
 * Section parallax reusable.
 * - src: gambar (public path)
 * - height: tinggi section
 * - speed: besar parallax (px)
 * - children: konten overlay
 */
export default function ParallaxStrip({ src, height = "70vh", speed = 80, children }) {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref, offset: ["start end", "end start"] });
  const y = useTransform(scrollYProgress, [0, 1], [-speed, speed]);

  return (
    <section ref={ref} className="relative w-full overflow-hidden rounded-2xl my-8" style={{ height }}>
      <motion.img
        src={src}
        alt=""
        aria-hidden
        className="absolute inset-0 w-full h-full object-cover"
        style={{ y }}
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/45 via-black/20 to-transparent" />
      <div className="relative z-10 h-full flex items-end md:items-center">
        <div className="p-6 md:p-10 text-white drop-shadow">
          {children}
        </div>
      </div>
    </section>
  );
}
