import React, { createContext, useContext, useEffect, useState } from "react";
import { supabase } from "../lib/supabaseClient.js";

const Ctx = createContext(null);
export const useAuth = () => useContext(Ctx);

export function AuthProvider({ children }) {
  const [session, setSession] = useState(null);
  const [profile, setProfile] = useState(null); // {role,email}

  useEffect(() => {
    supabase.auth.getSession().then(({ data }) => setSession(data.session || null));
    const { data: sub } = supabase.auth.onAuthStateChange((_evt, sess) => setSession(sess));
    return () => sub.subscription.unsubscribe();
  }, []);

  useEffect(() => {
    (async () => {
      if (!session?.user?.id) { setProfile(null); return; }
      const { data } = await supabase.from("profiles").select("email, role").eq("user_id", session.user.id).maybeSingle();
      setProfile(data || null);
    })();
  }, [session?.user?.id]);

  return <Ctx.Provider value={{ session, profile }}>{children}</Ctx.Provider>;
}
