import React, { useEffect, useMemo, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import usePackages from "../hooks/usePackages";
import { useCart } from "../context/CartContext";
import { useCurrency } from "../context/CurrencyContext";
import { formatMoneyFromIDR } from "../utils/currency";

/* Gambar hero — gunakan format yang sama supaya tidak shift */
const heroImages = ["/hero1.jpg","/hero2.jpg","/hero3.jpg","/hero4.jpg","/hero5.jpg","/hero6.jpg"];

export default function Home() {
  const { t } = useTranslation();
  const { rows: data } = usePackages();
  const { addItem } = useCart();
  const { fx, currency, locale } = useCurrency();

  /* ============================
     HERO CROSSFADE ANTI-FLICKER
     ============================ */
  const [idx, setIdx] = useState(0);
  const total = heroImages.length;
  const timerRef = useRef(null);
  const mountedRef = useRef(true);

  useEffect(() => {
    mountedRef.current = true;

    // Preload gambar pertama secara eager
    const first = new Image();
    first.decoding = "async";
    first.src = heroImages[0];

    const tick = async () => {
      const next = (idxRef.current + 1) % total;
      try {
        const img = new Image();
        img.decoding = "async";
        img.src = heroImages[next];
        // decode() lebih halus ketimbang onload untuk crossfade
        if (img.decode) { await img.decode(); }
      } catch(_) { /* ignore */ }

      if (mountedRef.current) setIdx(next);
    };

    // pakai ref untuk state agar interval stabil
    const idxRef = { current: 0 };
    const sync = () => { idxRef.current = idx; };

    const intervalStart = () => {
      timerRef.current = setInterval(tick, 4800);
    };

    intervalStart();
    return () => { mountedRef.current = false; clearInterval(timerRef.current); };
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [total]);

  const go = (d) => {
    clearInterval(timerRef.current);
    setIdx((i) => (i + d + total) % total);
    // restart interval setelah manual slide
    timerRef.current = setInterval(() => {
      setIdx((i) => (i + 1) % total);
    }, 4800);
  };

  /* ============================
     MOUSE PARALLAX (ringan)
     ============================ */
  const [mouse, setMouse] = useState({ x: 0, y: 0 });
  const onMouseMove = (e) => {
    const r = e.currentTarget.getBoundingClientRect();
    const mx = (e.clientX - r.left) / r.width - 0.5;
    const my = (e.clientY - r.top) / r.height - 0.5;
    setMouse({ x: mx, y: my });
  };

  /* Section ringan (tanpa framer) */
  const Section = ({ children, className = "" }) => (
    <section className={`section ${className}`}>{children}</section>
  );

  const [pax, setPax] = useState(2);

  const onAdd = (p, pricePerPaxIDR) => {
    // Hilangin fokus supaya nggak ada “outline flick”
    // & set immediate add tanpa re-route
    addItem({ id: p.id, title: p.locale?.title, price: pricePerPaxIDR, pax, qty: 1 });
  };

  return (
    <>
      {/* ===== HERO ===== */}
      <section
        onMouseMove={onMouseMove}
        className="relative h-[76vh] md:h-[86vh] overflow-hidden rounded-none"
      >
        {/* Crossfade layers */}
        <div className="absolute inset-0">
          {heroImages.map((src, i) => (
            <img
              key={src}
              src={src}
              alt=""
              aria-hidden
              className={`absolute inset-0 w-full h-full object-cover crossfade-layer transition-opacity duration-700 ${i===idx?'opacity-100':'opacity-0'}`}
              loading={i===0 ? "eager":"lazy"}
              decoding="async"
            />
          ))}
          {/* Overlay gradient supaya teks terbaca */}
          <div className="absolute inset-0 bg-gradient-to-b from-slate-950/55 via-slate-900/15 to-white/70 dark:to-slate-950/80 pointer-events-none" />
          {/* Haluskan texture */}
          <div className="absolute inset-0 opacity-20 grid-overlay pointer-events-none" />
        </div>

        {/* Blobs parallax (GPU friendly) */}
        <div
          className="absolute -top-20 -left-20 w-64 h-64 rounded-full bg-sky-400/30 blob"
          style={{ transform: `translate(${mouse.x*40}px, ${mouse.y*30}px)` }}
        />
        <div
          className="absolute top-24 -right-12 w-56 h-56 rounded-full bg-fuchsia-400/30 blob"
          style={{ transform: `translate(${-mouse.x*50}px, ${mouse.y*20}px)` }}
        />
        <div
          className="absolute bottom-10 left-1/2 -translate-x-1/2 w-[44rem] h-[44rem] rounded-full bg-amber-300/20 -z-[1] blob"
          style={{ transform: `translate(${mouse.x*20}px, ${-mouse.y*25}px)` }}
        />

        {/* Copy */}
        <div className="relative z-10 container h-full flex flex-col justify-center">
          <span className="badge mb-2">{t("hero.tag", { defaultValue: "BEST OFFERS" })}</span>
          <h1 className="mt-1 font-extrabold text-white leading-[1.05] text-4xl md:text-6xl">
            {t("hero.line1")} <span className="text-grad">{t("hero.line2")}</span>
          </h1>
          <p className="mt-3 max-w-xl text-white/90">{t("hero.desc")}</p>
          <div className="mt-6 flex gap-3">
            <a href="#packages" className="btn btn-primary pulse-soft no-tap-highlight btn-press"> {t("hero.ctaExplore")} </a>
            <Link to="/contact" className="btn btn-outline ring-hover no-tap-highlight"> {t("hero.ctaContact")} </Link>
          </div>
        </div>

        {/* Controls */}
        <div className="absolute inset-x-0 bottom-4 z-10 flex items-center justify-between container">
          <div className="flex gap-2">
            <button type="button" onClick={() => go(-1)} className="btn btn-outline glass press">‹</button>
            <button type="button" onClick={() => go(1)} className="btn btn-outline glass press">›</button>
          </div>
          <div className="flex gap-1.5">
            {heroImages.map((_, i) => (
              <button
                type="button"
                key={i}
                onClick={() => go(i-idx)}
                className={`h-2.5 w-2.5 rounded-full ring-1 ring-white/60 transition ${i===idx ? "bg-white" : "bg-white/30"}`}
                aria-label={`slide ${i+1}`}
              />
            ))}
          </div>
        </div>
      </section>

      {/* ===== ABOUT ===== */}
      <Section className="container mt-14">
        <div className="grid md:grid-cols-2 gap-8 items-start">
          <div className="tilt-1">
            <h2 className="text-2xl md:text-3xl font-bold"> {t("home.aboutTitle")} </h2>
            <p className="mt-3 text-slate-600 dark:text-slate-300">{t("home.aboutBody")}</p>
            <ul className="mt-4 space-y-2 text-slate-700 dark:text-slate-300 bullets">
              <li>• {t("home.point1")}</li>
              <li>• {t("home.point2")}</li>
              <li>• {t("home.point3")}</li>
              <li>• {t("home.point4")}</li>
            </ul>
          </div>
          <img
            src="/23.jpg"
            alt=""
            className="rounded-2xl shadow-smooth object-cover w-full h-64 md:h-80 img-fade"
            loading="lazy"
            decoding="async"
            onLoad={(e)=> e.currentTarget.classList.add("is-loaded")}
          />
        </div>
      </Section>

      {/* ===== PACKAGES ===== */}
      <Section id="packages" className="container mt-16">
        <div className="flex items-end justify-between mb-4">
          <h2 className="text-xl md:text-2xl font-bold">{t("home.popular")}</h2>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500 dark:text-slate-400">
              {t("home.calcFor")}
            </label>
            <select
              id="pax"
              value={pax}
              onChange={(e) => setPax(parseInt(e.target.value))}
              className="px-3 py-2 rounded-2xl"
            >
              {[1, 2, 3, 4, 5, 6].map((n) => (
                <option key={n} value={n}>
                  {n} {t("home.pax")}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          {data.map((p) => {
            const tier = (p.price_tiers || []).find((x) => x.pax === pax) || (p.price_tiers || [])[0];
            const pricePerPaxIDR = tier?.price_idr || 0;
            const spots = (p.locale?.spots || []).slice(0, 4).join(" • ");
            return (
              <article key={p.id} className="card card-hoverable card-press p-4">
                <h3 className="font-semibold text-lg mb-1">{p.locale?.title}</h3>
                <p className="text-sm text-slate-500 dark:text-slate-400 mb-2">{spots}...</p>
                <div className="flex items-center justify-between">
                  <p className="text-sky-600 dark:text-sky-400 font-bold">
                    {formatMoneyFromIDR(pricePerPaxIDR, currency, fx, locale)}{" "}
                    <span className="text-sm font-normal text-slate-500 dark:text-slate-400">/ {t("home.perPax")}</span>
                  </p>
                  <button
                    type="button"
                    className="btn btn-primary btn-press no-tap-highlight"
                    onClick={() => onAdd(p, pricePerPaxIDR)}
                  >
                    {t("home.addToCart")}
                  </button>
                </div>
              </article>
            );
          })}
        </div>
      </Section>

      {/* ===== CTA ===== */}
      <Section className="container mt-16 mb-20">
        <div className="card p-6 md:p-8 flex flex-col md:flex-row items-start md:items-center gap-5 card-inset">
          <div className="flex-1">
            <h3 className="text-xl md:text-2xl font-bold">{t("home.ctaTitle")}</h3>
            <p className="text-slate-600 dark:text-slate-300">{t("home.ctaDesc")}</p>
          </div>
          <div className="flex gap-3">
            <a href="https://wa.me/6289523949667" className="btn btn-primary no-tap-highlight" target="_blank" rel="noreferrer">
              WhatsApp
            </a>
            <Link to="/contact" className="btn btn-outline no-tap-highlight">
              {t("hero.ctaContact")}
            </Link>
          </div>
        </div>
      </Section>
    </>
  );
}
