import React from "react";
import { motion } from "framer-motion";
import { useTranslation } from "react-i18next";
import data from "../data/packages.json";
import { toIDR } from "../utils/format";

/** Variants untuk daftar + item (stagger) */
const listVariants = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { staggerChildren: 0.06, delayChildren: 0.05 } }
};
const itemVariants = {
  hidden: { opacity: 0, y: 10 },
  show: { opacity: 1, y: 0, transition: { duration: 0.28, ease: "easeOut" } }
};

// label lokal per bahasa (biar gak tergantung file i18n)
const LABELS = {
  en: {
    title: "Explore Packages",
    spots: "Attractions",
    day1: "Day 1",
    day2: "Day 2",
    snorkeling: "Snorkeling Spots",
    included: "Included",
    pricePerPax: "Price per Pax",
    note: "Note"
  },
  id: {
    title: "Explore Paket",
    spots: "Spot Wisata",
    day1: "Hari 1",
    day2: "Hari 2",
    snorkeling: "Spot Snorkeling",
    included: "Termasuk",
    pricePerPax: "Harga per Pax",
    note: "Catatan"
  },
  ja: {
    title: "パッケージ一覧",
    spots: "見どころ",
    day1: "1日目",
    day2: "2日目",
    snorkeling: "シュノーケリングスポット",
    included: "含まれるもの",
    pricePerPax: "料金（1人あたり）",
    note: "注意"
  }
};

export default function Explore() {
  const { i18n } = useTranslation();
  const lang = (i18n.language || "en").split("-")[0];
  const L = (obj) => (obj && (obj[lang] ?? obj.en)) || obj;

  return (
    <div className="container mt-6 space-y-4">
      <motion.h1
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.35, ease: "easeOut" }}
        className="text-2xl font-bold text-slate-900 dark:text-slate-100"
      >
        {LABELS[lang]?.title || LABELS.en.title}
      </motion.h1>

      <motion.div variants={listVariants} initial="hidden" animate="show" className="grid md:grid-cols-2 gap-4">
        {data.map(p => (
          <motion.div key={p.id} variants={itemVariants} className="card p-4">
            <details>
              <summary className="cursor-pointer font-semibold text-slate-900 dark:text-slate-100">
                {L(p.title)}
              </summary>

              <div className="mt-3 grid md:grid-cols-2 gap-3">
                <div>
                  {p.spots && (
                    <>
                      <h4 className="font-medium">{LABELS[lang]?.spots || LABELS.en.spots}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {L(p.spots).map((s, i) => <li key={i}>{s}</li>)}
                      </ul>
                    </>
                  )}

                  {p.spots_day1 && (
                    <>
                      <h4 className="font-medium">{LABELS[lang]?.day1 || LABELS.en.day1}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {L(p.spots_day1).map((s, i) => <li key={i}>{s}</li>)}
                      </ul>
                      <h4 className="font-medium mt-2">{LABELS[lang]?.day2 || LABELS.en.day2}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {L(p.spots_day2).map((s, i) => <li key={i}>{s}</li>)}
                      </ul>
                    </>
                  )}

                  {p.snorkeling && (
                    <>
                      <h4 className="font-medium mt-2">{LABELS[lang]?.snorkeling || LABELS.en.snorkeling}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {L(p.snorkeling).map((s, i) => <li key={i}>{s}</li>)}
                      </ul>
                    </>
                  )}
                </div>

                <div>
                  {p.include && (
                    <>
                      <h4 className="font-medium">{LABELS[lang]?.included || LABELS.en.included}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {L(p.include).map((s, i) => <li key={i}>{s}</li>)}
                      </ul>
                    </>
                  )}

                  {p.priceTiers && (
                    <>
                      <h4 className="font-medium mt-2">{LABELS[lang]?.pricePerPax || LABELS.en.pricePerPax}:</h4>
                      <ul className="list-disc ml-5 text-sm">
                        {p.priceTiers.map(([n, price]) => (
                          <li key={n}>{n} pax: {toIDR(price)}</li>
                        ))}
                      </ul>
                    </>
                  )}

                  {p.note && (
                    <p className="text-sm text-slate-500 dark:text-slate-400 mt-2">
                      {L(p.note)}
                    </p>
                  )}
                </div>
              </div>
            </details>
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
}
