import React, { useMemo, useState } from "react";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import HeroBackground from "../components/HeroBackground";
import { toIDR } from "../utils/format";
import { useCart } from "../context/CartContext";
import data from "../data/packages.json";

const heroImages = [
  "/hero1.png","/hero2.png","/hero3.png","/hero4.png","/hero5.png","/hero6.png"
];

/** Variants animasi */
const heroText = {
  hidden: { opacity: 0, y: 18 },
  show: { opacity: 1, y: 0, transition: { duration: 0.55, ease: "easeOut" } }
};
const listVariants = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { staggerChildren: 0.08, delayChildren: 0.05 } }
};
const itemVariants = {
  hidden: { opacity: 0, y: 10 },
  show: { opacity: 1, y: 0, transition: { duration: 0.3, ease: "easeOut" } }
};

// label lokal sederhana
const LABELS = {
  en: { popular: "Popular Packages", calcFor: "Calculate price for", paxSuffix: "/pax", addToCart: "Add to Cart" },
  id: { popular: "Paket Populer", calcFor: "Hitung harga untuk", paxSuffix: "/pax", addToCart: "Tambah ke Keranjang" },
  ja: { popular: "人気パッケージ", calcFor: "料金を計算", paxSuffix: "/人", addToCart: "カートに追加" }
};

export default function Home() {
  const { t, i18n } = useTranslation();
  const lang = (i18n.language || "en").split("-")[0];
  const L = (obj) => (obj && (obj[lang] ?? obj.en)) || obj;

  const { addItem } = useCart();
  const [pax, setPax] = useState(2);

  // daftar 23 gambar untuk konten panjang
  const gallery = useMemo(() => Array.from({ length: 23 }, (_, i) => `/${i + 1}.png`), []);

  return (
    <>
      {/* ===== HERO FULL-BLEED ===== */}
      <section className="relative h-[75vh] md:h-[85vh] w-full">
        <HeroBackground images={heroImages} />
        <motion.div
          variants={heroText}
          initial="hidden"
          animate="show"
          className="relative z-10 h-full flex items-center justify-center text-center px-4"
        >
          <div className="max-w-4xl mx-auto">
            <p className="text-white/80 text-sm md:text-base tracking-wide mb-2">
              CIDIKA TRAVEL&TOUR
            </p>
            <h1 className="text-4xl md:text-6xl font-extrabold leading-tight text-white drop-shadow-[0_2px_8px_rgba(0,0,0,0.45)]">
              {t("hero.line2")}
            </h1>
            <p className="mt-4 md:mt-5 text-white/90 md:text-lg drop-shadow-[0_1px_6px_rgba(0,0,0,0.35)]">
              {t("hero.desc")}
            </p>
            <div className="mt-6 flex items-center justify-center gap-3">
              <a href="#packages" className="btn btn-primary">{t("hero.ctaExplore")}</a>
              <a href="/contact" className="btn btn-outline bg-white/5 hover:bg-white/10 text-white border-white/30">
                {t("hero.ctaContact")}
              </a>
            </div>
          </div>
        </motion.div>
      </section>

      {/* ===== LONG CONTENT: GALLERY ===== */}
      <section className="container mt-12 md:mt-16">
        <h2 className="text-xl md:text-2xl font-bold mb-4 text-slate-900 dark:text-slate-100">
          {lang === "id" ? "Sorotan Destinasi" : lang === "ja" ? "おすすめスポット" : "Destination Highlights"}
        </h2>

        <div className="columns-1 sm:columns-2 lg:columns-3 gap-4 [column-fill:_balance]">
          {gallery.map((src, idx) => (
            <motion.img
              key={src}
              src={src}
              alt={`gallery-${idx + 1}`}
              loading="lazy"
              className="mb-4 w-full rounded-2xl shadow-smooth object-cover"
              initial={{ opacity: 0, scale: 0.98 }}
              whileInView={{ opacity: 1, scale: 1 }}
              viewport={{ once: true, amount: 0.2 }}
              transition={{ duration: 0.35, ease: "easeOut" }}
            />
          ))}
        </div>
      </section>

      {/* ===== PACKAGES ===== */}
      <section id="packages" className="container mt-16">
        <div className="flex items-end justify-between mb-4">
          <h2 className="text-xl md:text-2xl font-bold text-slate-900 dark:text-slate-100">
            {LABELS[lang]?.popular || LABELS.en.popular}
          </h2>
          <div className="flex items-center gap-2">
            <label htmlFor="pax" className="text-sm text-slate-500 dark:text-slate-400">
              {LABELS[lang]?.calcFor || LABELS.en.calcFor}
            </label>
            <select
              id="pax"
              value={pax}
              onChange={(e) => setPax(parseInt(e.target.value))}
              className="px-3 py-2 rounded-2xl"
            >
              {[1, 2, 3, 4, 5, 6].map(n => (
                <option key={n} value={n}>{n} pax</option>
              ))}
            </select>
          </div>
        </div>

        <motion.div variants={listVariants} initial="hidden" animate="show" className="grid md:grid-cols-2 gap-4">
          {data.map(p => {
            const tier = p.priceTiers?.find(x => parseInt(x[0]) === pax) || p.priceTiers?.[0];
            const price = tier ? tier[1] : 0;
            const preview =
              (p.spots && L(p.spots)) ||
              (p.spots_day1 && L(p.spots_day1)) ||
              [];
            return (
              <motion.article key={p.id} variants={itemVariants} className="card p-4">
                <h3 className="font-semibold text-lg mb-1 text-slate-900 dark:text-slate-100">
                  {L(p.title)}
                </h3>
                <p className="text-sm text-slate-500 dark:text-slate-400 mb-2">
                  {preview.slice(0, 4).join(" • ")}...
                </p>
                <div className="flex items-center justify-between">
                  <p className="text-sky-600 dark:text-sky-400 font-bold">
                    {toIDR(price)}{" "}
                    <span className="text-sm font-normal text-slate-500 dark:text-slate-400">
                      {LABELS[lang]?.paxSuffix || LABELS.en.paxSuffix}
                    </span>
                  </p>
                  <button
                    className="btn btn-primary"
                    onClick={() => addItem({ id: p.id, title: L(p.title), price, qty: 1 })}
                  >
                    {LABELS[lang]?.addToCart || LABELS.en.addToCart}
                  </button>
                </div>
              </motion.article>
            );
          })}
        </motion.div>
      </section>
    </>
  );
}
