// src/pages/Home.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import usePackages from "../hooks/usePackages";
import { useCurrency } from "../context/CurrencyContext";
import { formatMoneyFromIDR } from "../utils/currency";
import {
  Search,
  MapPin,
  Calendar as CalIcon,
  Users,
  Star,
  ShieldCheck,
  Wand2,
  PhoneCall,
  ThumbsUp,
  Route,
  ArrowRight,
} from "lucide-react";

/* =========================================================
   Assets & helpers
========================================================= */
const heroImages = [
  "/hero1.jpg",
  "/hero2.jpg",
  "/hero3.jpeg",
  "/hero4.jpg",
  "/hero5.jpg",
  "/hero6.jpeg",
];

function usePreload(images) {
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, [images]);
}

function getPkgImage(p) {
  const raw =
    p?.default_image ||
    p?.cover_url ||
    p?.thumbnail ||
    p?.thumb_url ||
    p?.image_url ||
    (Array.isArray(p?.images) && p.images[0]) ||
    (p?.data?.images && p.data.images[0]) ||
    "";
  if (!raw) return "/23.jpg";
  if (/^https?:\/\//i.test(raw)) return raw;
  return raw.startsWith("/") ? raw : `/${raw}`;
}

function useMouseParallax() {
  const [m, setM] = useState({ x: 0, y: 0 });
  useEffect(() => {
    const onMove = (e) => {
      const { innerWidth: w, innerHeight: h } = window;
      const x = ((e.clientX ?? w / 2) / w - 0.5) * 2;
      const y = ((e.clientY ?? h / 2) / h - 0.5) * 2;
      setM({ x, y });
    };
    window.addEventListener("mousemove", onMove, { passive: true });
    return () => window.removeEventListener("mousemove", onMove);
  }, []);
  return [m.x * 20, m.y * 14];
}

/* =========================================================
   Variants
========================================================= */
const fadeUp = {
  hidden: { opacity: 0, y: 14 },
  show: { opacity: 1, y: 0, transition: { duration: 0.5, ease: "easeOut" } },
};

const list = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { staggerChildren: 0.06, delayChildren: 0.08 } },
};

const card = {
  hidden: { opacity: 0, y: 12 },
  show: { opacity: 1, y: 0, transition: { duration: 0.38, ease: "easeOut" } },
};

/* =========================================================
   Home page
========================================================= */
export default function Home() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const { rows: data = [] } = usePackages();
  const { fx, currency, locale } = useCurrency();
  const [mx, my] = useMouseParallax();

  usePreload(heroImages);

  // Search form state
  const [keyword, setKeyword] = useState("");
  const [pax, setPax] = useState(2);
  const [date, setDate] = useState("");

  // Derive content
  const popular = useMemo(() => {
    const withPrice = (p) => {
      const pt = (p.price_tiers || [])[0];
      return { ...p, _price: pt?.price_idr || 0 };
    };
    return data.map(withPrice).sort((a, b) => a._price - b._price).slice(0, 6);
  }, [data]);

  const uniqueSpots = useMemo(() => {
    const set = new Set();
    data.forEach((p) => {
      const spots = p?.locale?.spots || p?.locales?.[0]?.spots || [];
      spots.forEach((s) => set.add(s));
    });
    return Array.from(set).slice(0, 18);
  }, [data]);

  const submitSearch = (e) => {
    e?.preventDefault?.();
    const params = new URLSearchParams();
    if (keyword.trim()) params.set("q", keyword.trim());
    navigate(`/explore?${params.toString()}`, {
      state: { pax, filterDate: date || undefined },
    });
  };

  return (
    <>
      {/* ================= HERO ================= */}
      <section className="relative h-[78vh] md:h-[86vh] overflow-hidden grain">
        {/* Background crossfade */}
        <HeroBackground images={heroImages} />

        {/* Color orbs parallax */}
        <div
          className="absolute -top-24 -left-24 w-80 h-80 rounded-full blur-3xl bg-sky-400/30"
          style={{ transform: `translate(${mx * 40}px, ${my * 30}px)` }}
        />
        <div
          className="absolute top-24 -right-16 w-72 h-72 rounded-full blur-3xl bg-fuchsia-400/30"
          style={{ transform: `translate(${-mx * 50}px, ${my * 22}px)` }}
        />
        <div
          className="absolute bottom-10 left-1/2 -translate-x-1/2 w-[48rem] h-[48rem] rounded-full blur-3xl bg-amber-300/20 -z-[1]"
          style={{ transform: `translate(${mx * 18}px, ${-my * 20}px)` }}
        />

        {/* Copy + Search panel */}
        <div className="relative z-10 container h-full flex flex-col justify-center">
          <motion.p
            variants={fadeUp}
            initial="hidden"
            animate="show"
            className="tracking-[0.28em] text-xs md:text-sm text-white/85"
          >
            {t("hero.tag", { defaultValue: "DISCOVER INDONESIA" })}
          </motion.p>

          <motion.h1
            variants={fadeUp}
            initial="hidden"
            animate="show"
            className="mt-2 font-extrabold text-white drop-shadow-[0_8px_24px_rgba(0,0,0,.35)] leading-[1.05] text-4xl md:text-6xl"
          >
            {t("hero.line1", { defaultValue: "Trips crafted for" })}{" "}
            <span className="text-sky-300">
              {t("hero.line2", { defaultValue: "memorable journeys" })}
            </span>
          </motion.h1>

          <motion.p
            variants={fadeUp}
            initial="hidden"
            animate="show"
            className="mt-3 max-w-2xl text-white/90"
          >
            {t("hero.desc", {
              defaultValue:
                "Paket liburan fleksibel, harga transparan, dan support ramah. Mulai petualanganmu sekarang.",
            })}
          </motion.p>

          {/* Search panel */}
          <motion.form
            onSubmit={submitSearch}
            variants={fadeUp}
            initial="hidden"
            animate="show"
            className="mt-6 bg-white/80 dark:bg-slate-900/70 backdrop-blur-md border border-white/30 dark:border-slate-800 rounded-2xl shadow-smooth p-3 md:p-4"
          >
            <div className="grid grid-cols-1 sm:grid-cols-5 gap-2 sm:gap-3">
              <div className="sm:col-span-2 flex items-center gap-2 px-3 py-2 rounded-xl bg-white/70 dark:bg-slate-900/60 border border-slate-200/70 dark:border-slate-800">
                <Search size={16} className="text-slate-500" />
                <input
                  value={keyword}
                  onChange={(e) => setKeyword(e.target.value)}
                  placeholder={t("home.searchPlaceholder", {
                    defaultValue: "Cari kota / spot (mis. Bromo)…",
                  })}
                  className="w-full bg-transparent outline-none text-sm"
                />
              </div>

              <label className="flex items-center gap-2 px-3 py-2 rounded-xl bg-white/70 dark:bg-slate-900/60 border border-slate-200/70 dark:border-slate-800">
                <CalIcon size={16} className="text-slate-500" />
                <input
                  type="date"
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                  className="w-full bg-transparent outline-none text-sm"
                />
              </label>

              <label className="flex items-center gap-2 px-3 py-2 rounded-xl bg-white/70 dark:bg-slate-900/60 border border-slate-200/70 dark:border-slate-800">
                <Users size={16} className="text-slate-500" />
                <select
                  value={pax}
                  onChange={(e) => setPax(parseInt(e.target.value))}
                  className="w-full bg-transparent outline-none text-sm"
                >
                  {[1, 2, 3, 4, 5, 6, 7, 8].map((n) => (
                    <option key={n} value={n}>
                      {n} {t("home.pax", { defaultValue: "pax" })}
                    </option>
                  ))}
                </select>
              </label>

              <button className="btn btn-primary glass w-full sm:w-auto">
                {t("hero.ctaExplore", { defaultValue: "Explore Now" })}
              </button>
            </div>

            {/* Stats mini */}
            <div className="mt-3 flex flex-wrap gap-3 text-[12px] text-slate-700/90 dark:text-slate-200/90">
              <span className="px-2.5 py-1 rounded-full bg-white/70 dark:bg-slate-800/70 border border-slate-200/70 dark:border-slate-700/60">
                <ThumbsUp size={12} className="inline mr-1" /> 1.2k+ happy travelers
              </span>
              <span className="px-2.5 py-1 rounded-full bg-white/70 dark:bg-slate-800/70 border border-slate-200/70 dark:border-slate-700/60">
                <ShieldCheck size={12} className="inline mr-1" /> Secure & trusted
              </span>
              <span className="px-2.5 py-1 rounded-full bg-white/70 dark:bg-slate-800/70 border border-slate-200/70 dark:border-slate-700/60">
                <Wand2 size={12} className="inline mr-1" /> Custom itinerary
              </span>
            </div>
          </motion.form>
        </div>

        {/* slide dots */}
        <div className="absolute inset-x-0 bottom-4 z-10 container flex justify-end">
          <div className="flex gap-1.5">
            {heroImages.map((_, i) => (
              <span key={i} className="h-1.5 w-6 rounded-full bg-white/50" />
            ))}
          </div>
        </div>
      </section>

      {/* =========== DESTINATIONS STRIP (scroll-x) =========== */}
      <section className="container mt-10">
        <motion.div variants={list} initial="hidden" whileInView="show" viewport={{ once: true }}>
          <motion.h2 variants={fadeUp} className="text-xl md:text-2xl font-bold">
            {t("home.destinations", { defaultValue: "Popular Destinations" })}
          </motion.h2>

          <div className="mt-4 overflow-x-auto hide-scrollbar">
            <div className="flex gap-3 snap-x">
              {uniqueSpots.map((s, i) => (
                <motion.div
                  key={i}
                  variants={card}
                  className="snap-start shrink-0 px-3 py-2 rounded-2xl bg-slate-50 dark:bg-slate-800 border border-slate-200/70 dark:border-slate-700/60 text-sm flex items-center gap-2 hover-lift"
                >
                  <MapPin size={14} className="text-sky-600" />
                  <span className="whitespace-nowrap">{s}</span>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.div>
      </section>

      {/* =========== FEATURED PACKAGES GRID =========== */}
      <section className="container mt-12" id="packages">
        <div className="flex items-end justify-between mb-3">
          <h2 className="text-xl md:text-2xl font-bold">
            {t("home.popular", { defaultValue: "Featured Packages" })}
          </h2>
          <Link to="/explore" className="text-sm text-sky-700 dark:text-sky-300 inline-flex items-center gap-1">
