                      {t("explore.seeDetail", { defaultValue: "See Detail" })}
                    </Link>
                    <Link to="/contact" className="text-sm text-slate-500 hover:underline">
                      {t("home.ask", { defaultValue: "Ask a question" })}
                    </Link>
                  </div>
                </div>
              </motion.article>
            );
          })}
        </motion.div>
      </section>

      {/* =========== WHY US (icons) =========== */}
