      {/* =========== TESTIMONIALS =========== */}
      <section className="container mt-14">
        <motion.h2 variants={fadeUp} initial="hidden" whileInView="show" viewport={{ once: true }} className="text-xl md:text-2xl font-bold">
          {t("home.testimonials", { defaultValue: "What travelers say" })}
        </motion.h2>
        <motion.div
          variants={list}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true, amount: 0.2 }}
          className="mt-4 grid md:grid-cols-3 gap-4"
