        </motion.div>
      </section>

      {/* =========== CTA =========== */}
      <section className="container mt-16 mb-20">
        <motion.div
          variants={fadeUp}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true }}
          className="card p-6 md:p-8 flex flex-col md:flex-row items-start md:items-center gap-5 hover-lift bg-gradient-to-br from-sky-50 to-white dark:from-slate-900 dark:to-slate-950"
